ExprLine.OrigStr:
DeclareType("GenInstanceData", "PowerListNode");
if (PowersUI_UseStatDescription(GenInstanceData))
	GenSetString(LayoutBox.Parent.Parent.Parent, "PowerName", GenInstanceData.PowerDef);
	GenSetString(LayoutBox.Parent.Parent.Parent, "NodeName", GenInstanceData.NodeDef);
	GenSetString(LayoutBox.Parent.Parent.Parent, "GroupName", GenInstanceData.GroupDef);
else
	if (GenHasVar(Self.Parent, "DescriptionTarget"))
		GenSetString(Gen(Self.Parent.Var[DescriptionTarget].String), "DescriptionText",
			PowersUI_GetPowerListNodeDescription(Player, GenInstanceData, "PowersUI_Description_Available"));
	endif
endif

if (GenGetNumOwnedPowers(Player) > Self.Parent.Var[PowerNumber].Int)
	GenRunCommand(Self, "Undo_Buy_PowerTreeNode");
endif

GenRunCommand(Self,
	      StringConcat("Buy_PowerTreeNode ",
	      StringConcat(GenInstanceData.TreeDef,
	      StringConcat(" ", GenInstanceData.NodeDef))));
