ExprLine.OrigStr:
DeclareType("GenInstanceData", "CharacterPathNode");

GenSetState(Gen("CharacterCreation_Framework_FreeFormButton"), Selected, False);
GenSendMessage(Gen("CharacterCreation_Framework_Root"), "Archetype");
GenSendMessage(Gen("CharacterEditor_Root"), "Reset");

CharacterCreation_ClearPowers(Player);
CharacterCreation_SaveCharacterPath(GenInstanceData.Path.Name);
CharacterCreation_AutoBuyRecommendedPowersByCostTable("Endbuildpoints");
CharacterCreation_AutoBuyRecommendedPowersByCostTable("Treepoints");
CharacterCreation_AutoBuyRecommendedPowersByCostTable("Originpoints");

CostumeCreator_UpdatePowerFXBonesFromCharacterCreation();

GenSetString(Gen("CharacterCreation_Framework_Root"), "FrameworkName", Translate(GenInstanceData.Path.DisplayName.Message));
GenSetString(Gen("CharacterCreation_Framework_Root"), "GroupName", Layoutbox.Parent.Var[Groupname].String);

if ( Layoutbox.Parent.Var[IsReawakened].Int = 1 )
	GenSetString(Gen("CharacterCreation_Framework_Root"), "GroupIcon", GenInstanceData.Path.LargeImage);
	GenSetString(Gen("CharacterCreation_Framework_Root"), "Header1", Translate(GenInstanceData.Path.Header1.Message));

	GenSetValue(Gen("CharacterCreation_Framework_Root"), "IsReawakened", 1);
else
	GenSetString(Gen("CharacterCreation_Framework_Root"), "GroupIcon", Layoutbox.Parent.Var[SmallIconName].String);
	GenSetValue(Gen("CharacterCreation_Framework_Root"), "IsReawakened", 0);

endif


GenSetString(Gen("CharacterCreation_Framework_Root"), "Description1", Translate(GenInstanceData.Path.Description1.Message));
GenSetString(Gen("CharacterCreation_Framework_Root"), "Header2", Translate(GenInstanceData.Path.Header2.Message));
GenSetString(Gen("CharacterCreation_Framework_Root"), "Description2", Translate(GenInstanceData.Path.Description2.Message));
GenSetString(Gen("CharacterCreation_Framework_Root"), "Description3", Translate(GenInstanceData.Path.Description3.Message));
GenSetString(Gen("CharacterCreation_Framework_Root"), "FlavorText", Translate(GenInstanceData.Path.FlavorText.Message));
GenSetValue(Gen("CharacterCreation_Framework_Root"), "CanUse", GenInstanceData.CanUse);

DeclareType("GenInstanceData", "CharacterPathNode");
GenSetState(Self, Selected, Gen("CharacterCreation_Framework_Root").Var[SelectedPath].String = GenInstanceData.Path.Name);
if (InState(Selected))
	GenSetValue(Gen("CharacterCreation_Framework_Root"), "CanPurchase", GenInstanceData.CanPurchase);
	GenSetString(Gen("CharacterCreation_Framework_InfoBox"), "ErrorString", GenInstanceData.estrError);
	GenSetString(Gen("CharacterCreation_Framework_Root"), "ItemName", GenInstanceData.ProductName);
	GenSetValue(Gen("CharacterCreation_Framework_Root"), "CanUse", GenInstanceData.CanUse);
endif
if (InState(MouseOver))
	GenSetString(Gen("CharacterCreation_Framework_Root"), "MouseoverPath", GenInstanceData.Path.Name);
	GenSetString(Gen("CharacterCreation_Framework_Root"), "MouseoverFrameworkName", Translate(GenInstanceData.Path.DisplayName.Message));
	GenSetString(Gen("CharacterCreation_Framework_Root"), "MouseoverGroupName", Layoutbox.Parent.Var[Groupname].String);
	GenSetString(Gen("CharacterCreation_Framework_Root"), "MouseoverGroupIcon", Layoutbox.Parent.Var[SmallIconName].String);

	if ( Layoutbox.Parent.Var[IsReawakened].Int = 1 )
		GenSetString(Gen("CharacterCreation_Framework_Root"), "MouseoverGroupIcon", GenInstanceData.Path.LargeImage);
		GenSetString(Gen("CharacterCreation_Framework_Root"), "MouseoverHeader1", Translate(GenInstanceData.Path.Header1.Message));

		GenSetValue(Gen("CharacterCreation_Framework_Root"), "MouseoverIsReawakened", 1);
	else
		GenSetString(Gen("CharacterCreation_Framework_Root"), "MouseoverGroupIcon", Layoutbox.Parent.Var[SmallIconName].String);
		GenSetValue(Gen("CharacterCreation_Framework_Root"), "MouseoverIsReawakened", 0);
	endif


	GenSetString(Gen("CharacterCreation_Framework_Root"), "MouseoverDescription1", Translate(GenInstanceData.Path.Description1.Message));
	GenSetString(Gen("CharacterCreation_Framework_Root"), "MouseoverHeader2", Translate(GenInstanceData.Path.Header2.Message));
	GenSetString(Gen("CharacterCreation_Framework_Root"), "MouseoverDescription2", Translate(GenInstanceData.Path.Description2.Message));
	GenSetString(Gen("CharacterCreation_Framework_Root"), "MouseoverDescription3", Translate(GenInstanceData.Path.Description3.Message));
	GenSetString(Gen("CharacterCreation_Framework_Root"), "MouseoverFlavorText", Translate(GenInstanceData.Path.FlavorText.Message));
	GenSetString(Gen("CharacterCreation_Framework_Root"), "MouseoverItemName", GenInstanceData.ProductName);
	GenSetValue(Gen("CharacterCreation_Framework_Root"), "MouseoverCanPurchase", GenInstanceData.CanPurchase);
	GenSetValue(Gen("CharacterCreation_Framework_Root"), "MouseoverCanUse", GenInstanceData.CanUse);
endif
