ExprLine.OrigStr:
if (Self.Parent.Var[ColorID].Int < 120)
	GenCheckButtonSetState(Self, CostumeCreator_LockSharedColorCheck(Self, Self.Var[LockID].Int) = CostumeLockState_Checked);
	GenCheckButtonSetInconsistent(Self, CostumeCreator_LockSharedColorCheck(Self, Self.Var[LockID].Int));
else
	GenCheckButtonSetState(Self, CostumeCreator_LockStyleCheck(Self, Self.Var[LockID].Int) = CostumeLockState_Checked);
	GenCheckButtonSetInconsistent(Self, CostumeCreator_LockStyleCheck(Self, Self.Var[LockID].Int) = CostumeLockState_Partial);
endif
GenSetState(Self, User, Self.State.Checked or Self.State.Inconsistent);

if (Self.Parent.Var[ColorID].Int < 120)
	CostumeCreator_LockSharedColorToggle(Self, Self.Var[LockID].Int);
else
	CostumeCreator_SetColorLink(CostumeColorLink_None);
	CostumeCreator_LockStyleToggle(Self, Self.Var[LockID].Int);

	if (CostumeCreator_LockStyleCheck(Self, Self.Var[LockID].Int) = CostumeLockState_Unchecked and not GenInGlobalState(CharacterEditor_Weapons))
		CostumeCreator_SetPartColorLinkIfCommon(Self, Self.Parent.Var[ColorID].Int, 0, 0, 0, 0, 0, 0);
	endif
endif

if (Self.Parent.Var[ColorID].Int = 100)
	GenSetValue(Self, "LockID", CostumeLock_SkinColor);
elif (Self.Parent.Var[ColorID].Int = 110)
	GenSetValue(Self, "LockID", CostumeLock_SharedColor0);
elif (Self.Parent.Var[ColorID].Int = 111)
	GenSetValue(Self, "LockID", CostumeLock_SharedColor1);
elif (Self.Parent.Var[ColorID].Int = 112)
	GenSetValue(Self, "LockID", CostumeLock_SharedColor2);
elif (Self.Parent.Var[ColorID].Int = 113)
	GenSetValue(Self, "LockID", CostumeLock_SharedColor3);
elif (Self.Parent.Var[ColorID].Int = 120)
	GenSetValue(Self, "LockID", CostumeLock_Color0);
elif (Self.Parent.Var[ColorID].Int = 121)
	GenSetValue(Self, "LockID", CostumeLock_Color1);
elif (Self.Parent.Var[ColorID].Int = 122)
	GenSetValue(Self, "LockID", CostumeLock_Color2);
elif (Self.Parent.Var[ColorID].Int = 123)
	GenSetValue(Self, "LockID", CostumeLock_Color3);
endif

GenSetString(Self, "Option", Translate("CharacterEditor.Lock.Color"));
if (CostumeCreator_GetLayerListSize() = 0 or not CostumeCreator_GetCurrentLayer())
	GenSetString(Self, "Inconsistent", "");
elif (CostumeCreator_GetCurrentLayer().LayerType)
	GenSetString(Self, "Inconsistent", "");
else
	GenSetString(Self, "Inconsistent", Translate("CharacterEditor.Lock.ColorInconsistent"));
endif
if (Self.Parent.Var[ColorID].Int = 100)
	GenSetString(Self, "Inconsistent", "");
elif (Self.Parent.Var[ColorID].Int = 110)
	GenSetString(Self, "Inconsistent", "");
elif (Self.Parent.Var[ColorID].Int = 111)
	GenSetString(Self, "Inconsistent", "");
elif (Self.Parent.Var[ColorID].Int = 112)
	GenSetString(Self, "Inconsistent", "");
elif (Self.Parent.Var[ColorID].Int = 113)
	GenSetString(Self, "Inconsistent", "");
endif

if (GenInState(Self, User))
	if ((not Self.Var[OldColorValue].Int = CostumeCreator_GetEditColorValue(Self.Var[ColorID].Int)) or
			(CostumeCreator_MaterialHasGlow(Self.Var[ColorID].Int) and
				not Self.Var[OldGlowValue].Int = CostumeCreator_GetGlow(Self.Var[ColorID].Int))
	   )
		GenSendMessage(Gen("CharacterEditor_Root"), "Checkpoint");
	endif

	GenSetState(Self, User, 0);
endif

GenSetValue(Self, "OldColorValue", CostumeCreator_GetEditColorValue(Self.Var[ColorID].Int));
GenSetValue(Self, "OldGlowValue", CostumeCreator_GetGlow(Self.Var[ColorID].Int));
GenSetState(Self, User, 1);

if (Self.Var[ColorID].Int = 100)
	GenSetString(Gen("CharacterEditor_CostumeEditor_SkinColorChooser"), "GenToPopupAround", GenGetName(Self));
	GenSetValue(Gen("CharacterEditor_CostumeEditor_SkinColorChooser"), "ColorID", Self.Var[ColorID].Int);
	GenSendMessage(Gen("CharacterEditor_CostumeEditor_SkinColorChooser"), "PopupAt");
else
	GenSetString(Gen("CharacterEditor_CostumeEditor_ColorChooser"), "GenToPopupAround", GenGetName(Self));
	GenSetValue(Gen("CharacterEditor_CostumeEditor_ColorChooser"), "ColorID", Self.Var[ColorID].Int);
	GenSendMessage(Gen("CharacterEditor_CostumeEditor_ColorChooser"), "PopupAt");
endif
