ExprLine.OrigStr:
if (GenSetMicroTransProduct(Self, Self.Var[MicroTransactionID].Int))
	DeclareType("GenData", "MicroTransactionUIProduct");
	GenSetString(Self, "Price", FormatInt(GenData.Price));
	if (InState(User2))
		Translate("CharacterEditor_CostumeEditor_Unlock_Cstore_Unowned")
	else
		Translate("CharacterEditor_CostumeEditor_Unlock_Cstore_Owned")
	endif
else
	""
endif

DeclareType("RowData", "PCGeometryDef");
GenSetState(Self, User, not (CostumeCreator_GeoIsPlayerInitial(RowData.Name) or RowData.Name = "None"));

# Hacky override to cause the "None" geometry for the weapon to appear as "Default"
GenSetState(Self, User1, RowData.Name = "None" and CostumeCreator_GetEditRegion().Name = "Weapons");

# Set the UnlockMetaData
CostumeCreator_GetGeoUnlock(Self, RowData.Name);

GenSetString(Self, "Name", RowData.Name);
if (InState(User1))
	if (WeaponStanceName(CostumeState.Bone) = "")
		GenSetString(Self, "DisplayName", "CharacterEditor.CostumeEditor.WeaponPartDefault");
	else
		GenSetString(Self, "DisplayName", WeaponStanceName(CostumeState.Bone));
	endif
else
	GenSetString(Self, "DisplayName", RowData.DisplayNameMsg.Message);
endif

DeclareType("RowData", "PCMaterialDef");
GenSetState(Self, User, not (CostumeCreator_MatIsPlayerInitial(RowData.Name) or RowData.Name = "None"));

# Set the UnlockMetaData
CostumeCreator_GetMatUnlock(Self, RowData.Name);

GenSetString(Self, "Name", RowData.Name);
GenSetString(Self, "DisplayName", RowData.DisplayNameMsg.Message);

DeclareType("RowData", "PCTextureDef");
GenSetState(Self, User, not (CostumeCreator_TexIsPlayerInitial(RowData.Name) or RowData.Name = "None"));

# Set the UnlockMetaData
CostumeCreator_GetTexUnlock(Self, RowData.Name);

GenSetString(Self, "Name", RowData.Name);
GenSetString(Self, "DisplayName", RowData.DisplayNameMsg.Message);

DeclareType("RowData", "PCGeometryDef");
CostumeCreator_SetGeo(RowData.Name);

if (CostumeCreator_GetEditRegion().Name = "Weapons" and RowData.Name = "None")
	CostumeCreator_SetColorLink(CostumeColorLink_All);
endif

DeclareType("RowData", "PCTextureDef");
if (List.Parent.Parent.Var[LockID].Int = CostumeLock_Pattern)
	CostumeCreator_SetPattern(RowData.Name);
elif (List.Parent.Parent.Var[LockID].Int = CostumeLock_Detail)
	CostumeCreator_SetDetail(RowData.Name);
elif (List.Parent.Parent.Var[LockID].Int = CostumeLock_Specular)
	CostumeCreator_SetSpecular(RowData.Name);
elif (List.Parent.Parent.Var[LockID].Int = CostumeLock_Diffuse)
	CostumeCreator_SetDiffuse(RowData.Name);
endif

DeclareType("RowData", "PCTextureDef");
if (List.Parent.Parent.Var[LockID].Int = CostumeLock_Pattern)
	CostumeCreator_SetHoverPattern(RowData.Name);
elif (List.Parent.Parent.Var[LockID].Int = CostumeLock_Detail)
	CostumeCreator_SetHoverDetail(RowData.Name);
elif (List.Parent.Parent.Var[LockID].Int = CostumeLock_Specular)
	CostumeCreator_SetHoverSpecular(RowData.Name);
elif (List.Parent.Parent.Var[LockID].Int = CostumeLock_Diffuse)
	CostumeCreator_SetHoverDiffuse(RowData.Name);
endif

GenSendMessage(Self, "Create" + List.Var[Type].String);

if (Self.Var[Name].String = List.Parent.Parent.Var[CurrentName].String)
	GenListSetSelectedRow(List, Self.State.Row);
endif

# Get the display name
if (not InState(User1))
	GenSetString(Self, "DisplayName", Translate(Self.Var[DisplayName].String));
elif (Self.Var[DisplayName].String = "CharacterEditor.CostumeEditor.WeaponPartDefault")
	GenSetString(Self, "DisplayName", Translate("CharacterEditor.CostumeEditor.WeaponPartDefault"));
endif

# Harvest information from the UnlockMetaData
DeclareType("GenData", "UnlockMetaData");
if (GenData)
	GenSetState(Self, Microtransacted, not GenData.MicrotransactionID = 0);
	GenSetState(Self, User2, not GenData.Owned);
	GenSetState(Self, User3, GenData.Owned);
	GenSetValue(Self, "MicroTransactionID", GenData.MicrotransactionID);
endif

# Format it
if (Player) # This check should not be necessary... This is just a bandaid
	if (List.Var[Type].String = "Tex" and Self.Var[Name].String = guild_getemblemname(Player))
		GenSetString(Self, "DisplayName", "<font color=Con_Extremely_Hard_Dark>" + Self.Var[DisplayName].String + "</font>");
	elif (GenInState(Self, User2))
		GenSetString(Self, "DisplayName", "<font color=#cccccc>" + Self.Var[DisplayName].String + "</font>");
	endif
else
	GenSetString(Self, "DisplayName", "");
endif
