ExprLine.OrigStr:
GenSetState(Self, Disabled, CostumeCreator_GetDetailListSize() <= 1);
GenSetString(Self, "CurrentName", CostumeCreator_GetTexSysName(CostumeTexture_Detail));
GenSetString(Self, "CurrentSelection", CostumeCreator_GetTexName(CostumeTexture_Detail));
if (Self.Var[CurrentName].String = guild_getemblemname(Player))
	GenSetString(Self, "CurrentSelectionFormatted", "<font color=Con_Extremely_Hard_Dark>" + Self.Var[CurrentSelection].String + "</font>");
elif (not CostumeCreator_TexIsPlayerInitial(Self.Var[CurrentName].String))
	GenSetString(Self, "CurrentSelectionFormatted", "<font color=Success>" + Self.Var[CurrentSelection].String + "</font>");
else
	GenSetString(Self, "CurrentSelectionFormatted", Self.Var[CurrentSelection].String);
endif

if (CostumeCreator_GetTexUnlock(Self, CostumeCreator_GetTexSysName(CostumeTexture_Detail)))
	DeclareType("GenData", "UnlockMetaData");
	GenSetState(Gen("CharacterEditor_CostumeEditor_TextureDetailSelector"), Microtransacted, not GenData.MicrotransactionID = 0);
	GenSetState(Gen("CharacterEditor_CostumeEditor_TextureDetailSelector"), User2, not GenData.Owned);
	GenSetState(Gen("CharacterEditor_CostumeEditor_TextureDetailSelector"), User3, GenData.Owned);
else
	GenSetState(Gen("CharacterEditor_CostumeEditor_TextureDetailSelector"), Microtransacted, False);
	GenSetState(Gen("CharacterEditor_CostumeEditor_TextureDetailSelector"), User2, False);
	GenSetState(Gen("CharacterEditor_CostumeEditor_TextureDetailSelector"), User3, False);
endif
