ExprLine.OrigStr:
DeclareType("GenInstanceData", "CharacterSelectionSlot");
GenSetString(Gen("CharacterSelection_Name"), "Text", GenInstanceData.Choice.name);
GenSetString(Gen("CharacterSelection_Location"), "Text", TranslateMapName(GenInstanceData.Choice.MapName));
GenSetString(Gen("CharacterSelection_Level"), "Text", MessageFormatInt("CharacterSelection_Level", GenInstanceData.Choice.Level));
if (GenInstanceData.Choice.uiLastPlayedTime = 0)
	GenSetString(Gen("CharacterSelection_LastLogin"), "Text", Translate("CharacterSelection_LastPlayedToday"));
elif (TimeSecondsSince2000("Now") - GenInstanceData.Choice.uiLastPlayedTime < 86400)
	GenSetString(Gen("CharacterSelection_LastLogin"), "Text", Translate("CharacterSelection_LastPlayedToday"));
elif (TimeSecondsSince2000("Now") - GenInstanceData.Choice.uiLastPlayedTime < 172800)
	GenSetString(Gen("CharacterSelection_LastLogin"), "Text", Translate("CharacterSelection_LastPlayedYesterday"));
else
	GenSetString(Gen("CharacterSelection_LastLogin"), "Text", MessageFormatInt("CharacterSelection_LastPlayed", Int(Floor((TimeSecondsSince2000("Now") - GenInstanceData.Choice.uiLastPlayedTime) / 86400))));
endif
GenSetString(Gen("CharacterSelection_CreatedOn"), "Text", MessageFormatDateTime("CharacterSelection_CreatedOn", GenInstanceData.Choice.uiCreatedTime));
GenSetString(Gen("CharacterSelection_PlayerType"), "Text", TranslateStaticDefineInt("PlayerType", GenInstanceData.Choice.PlayerType));
if (GenInstanceData.Choice.CharacterPath = "")
	GenSetString(Gen("CharacterSelection_CharacterPath"), "Text", Translate("CharacterSelection_Freeform"));
	GenSetString(Gen("CharacterSelection_FreeTimeLeft"), "Text", "");
else
	GenSetString(Gen("CharacterSelection_CharacterPath"), "Text", TranslateDisplayMessage(GenInstanceData.Choice.CharacterPath.DisplayName));
	if ( IsCharacterPathFreeNotOwnedByName(GenInstanceData.Choice.CharacterPath) )
		if (GamePermissionTimedSecondsLeftByName( GenInstanceData.Choice.CharacterPath ) > 0 )
			GenSetString(Gen("CharacterSelection_FreeTimeLeft"), "Text", MessageFormatTimer("CharacterCreation_FreeAtTimeLeft", GamePermissionTimedSecondsLeftByName(GenInstanceData.Choice.CharacterPath) ));
		else
			GenSetString( Gen("CharacterSelection_FreeTimeLeft"), "Text", Translate("CharacterCreation_FreeAtTimeIsOut" ) );
		endif
	else
		GenSetString(Gen("CharacterSelection_FreeTimeLeft"), "Text", "");
	endif
endif

DeclareType("GenInstanceData", "CharacterSelectionSlot");
if (GenInState(Self.Parent, Selected) and LayoutBox.Var[MouseOverIndex].Int = -1)
	GenSendMessage(Self, "SetSideInfo");
	GenSetString(LayoutBox, "SelectedName", GenInstanceData.Choice.name);
	GenSetValue(LayoutBox, "SelectedContainerID", GenInstanceData.Choice.ID);
	GenSetValue(LayoutBox, "NewCharacter", False);
	GenSetValue(LayoutBox, "HideCostume", False);
	GenSetValue(LayoutBox, "SelectedPlayerType", GenInstanceData.Choice.PlayerType);
	GenSetString(LayoutBox, "CharacterPath", GenInstanceData.Choice.CharacterPath);
	GenSetValue(LayoutBox, "Level", GenInstanceData.Choice.Level);
	Login_PreChooseCharacterByID(GenInstanceData.Choice.ID);
endif
if (GenGetIntVar(Self, "MouseOver"))
	GenSendMessage(Self, "SetSideInfo");
	GenSetValue(LayoutBox, "MouseoverIndex", GenInstanceNumber + (LayoutBox.Var[PageNumber].Int*13));
	GenSetValue(LayoutBox, "NewCharacter", False);
	GenSetValue(LayoutBox, "HideCostume", False);
	GenSetValue(LayoutBox, "MouseOverID", GenInstanceData.Choice.ID);
	GenSetValue(LayoutBox, "MouseOverPlayerType", GenInstanceData.Choice.PlayerType);
	GenSetValue(LayoutBox, "Level", GenInstanceData.Choice.Level);
endif

if (GenInstanceData.Choice.PlayerType = PlayerTypeSuperPremium)
	if (GenGetIntVar(Self, "MouseOver"))
		GenSpriteSetBasicTextureByName(Self, "CC_Selection_Frame_Hex_Portrait_Gold_Freeform_Mouseover");
	else
		GenSpriteSetBasicTextureByName(Self, "CC_Selection_Frame_Hex_Portrait_Gold_Freeform_Idle");
	endif
elif (GenInstanceData.Choice.PlayerType = PlayerTypePremium)
	if (GenGetIntVar(Self, "MouseOver"))
		GenSpriteSetBasicTextureByName(Self, "CC_Selection_Frame_Hex_Portrait_Gold_Mouseover");
	else
		GenSpriteSetBasicTextureByName(Self, "CC_Selection_Frame_Hex_Portrait_Gold_Idle");
	endif
else
	if (GenGetIntVar(Self, "MouseOver"))
		GenSpriteSetBasicTextureByName(Self, "CC_Selection_Frame_Hex_Portrait_Silver_Mouseover");
	else
		GenSpriteSetBasicTextureByName(Self, "CC_Selection_Frame_Hex_Portrait_Silver_Idle");
	endif
endif

DeclareType("GenInstanceData", "CharacterSelectionSlot");
if (GenInState(Self.Parent, Selected))
	if (LayoutBox.Var[MouseOverIndex].Int = -1)
		GenSendMessage(Self, "SetSideInfo");
	endif
	GenSetString(LayoutBox, "SelectedName", "");
	GenSetValue(LayoutBox, "SelectedContainerID", -1);
	GenSetValue(LayoutBox, "HideCostume", True);
	GenSetValue(LayoutBox, "SuperPremium", GenInstanceData.SuperPremium);
	GenSetValue(Gen("CharacterSelection_List"), "NewCharacter", True);
endif
if (GenGetIntVar(Self, "MouseOver"))
	if (LayoutBox.Var[MouseOverIndex].Int = -1)
		GenSendMessage(Self, "SetSideInfo");
	endif
	GenSetValue(LayoutBox, "MouseoverIndex", GenInstanceNumber + (LayoutBox.Var[PageNumber].Int*13));
	GenSetValue(LayoutBox, "HideCostume", True);
	GenSetValue(LayoutBox, "SuperPremium", GenInstanceData.SuperPremium);
	GenSetValue(Gen("CharacterSelection_List"), "NewCharacter", True);
endif

if (LoginGetPlayerType() = PlayerTypePremium)
	if (GenGetIntVar(Self, "MouseOver"))
		if (GenInstanceData.SuperPremium)
			GenSpriteSetBasicTextureByName(Self, "CC_Selection_Frame_Hex_Portrait_Gold_Freeform_Mouseover");
		else
			GenSpriteSetBasicTextureByName(Self, "CC_Selection_Frame_Hex_Portrait_Gold_Idle");
		endif
	else
		if (GenInstanceData.SuperPremium)
			GenSpriteSetBasicTextureByName(Self, "CC_Selection_Frame_Hex_Portrait_Gold_Freeform_Idle");
		else
			GenSpriteSetBasicTextureByName(Self, "CC_Selection_Frame_Hex_Portrait_Gold_Idle");
		endif
	endif
else
	if (GenGetIntVar(Self, "MouseOver"))
		if (GenInstanceData.SuperPremium)
			GenSpriteSetBasicTextureByName(Self, "CC_Selection_Frame_Hex_Portrait_Silver_Freeformslot_Mouseover");
		else
			GenSpriteSetBasicTextureByName(Self, "CC_Selection_Frame_Hex_Portrait_Silver_Mouseover");
		endif
	else
		if (GenInstanceData.SuperPremium)
			GenSpriteSetBasicTextureByName(Self, "CC_Selection_Frame_Hex_Portrait_Silver_Freeformslot_Idle");
		else
			GenSpriteSetBasicTextureByName(Self, "CC_Selection_Frame_Hex_Portrait_Silver_Idle");
		endif
	endif
endif

if (InState(Selected) and LayoutBox.Var[MouseOverIndex].Int = -1)
	GenSetString(Self.Parent, "SelectedName", "");
	GenSetValue(Self.Parent, "SelectedContainerID", -1);
	GenSetValue(LayoutBox, "HideCostume", True);
endif
if (GenGetIntVar(Self, "MouseOver"))
	GenSetValue(LayoutBox, "MouseoverIndex", GenInstanceNumber + (LayoutBox.Var[PageNumber].Int*13));
	GenSetValue(LayoutBox, "HideCostume", True);
endif
