ExprLine.OrigStr:
DeclareType("GenInstanceData", "CharacterPathNode");
GenSetString(LayoutBox, "PathName", GenInstanceData.Path.Name);
GenSetValue(LayoutBox, "CanUse", GenInstanceData.CanUse);
GenSendMessage(LayoutBox, "SetPath");

DeclareType("GenInstanceData", "CharacterPathNode");
GenSetState(Self, Disabled, not GenInstanceData.CanUse
	or GenInstanceData.Path.Name = Gen("CharacterSelection_List").Var[CharacterPath].String
	or not Gen("CharacterSelection_PlayerTypeConvertDialog_NewArchRB").State.Checked);

GenSetState(Self, User, not GenInstanceData.CanUse);

if(not Gen("CharacterSelection_PlayerTypeConvertDialog_NewArchRB").State.Checked)
	GenSetString(Self, "DisableReason", Translate("CharacterPath_WrongOption"));
elif( not GenInstanceData.CanUse )
	GenSetString(Self, "DisableReason", Translate("CharacterPath_CannotUse"));
elif(GenInstanceData.Path.Name = Gen("CharacterSelection_List").Var[CharacterPath].String)
	GenSetString(Self, "DisableReason", Translate("CharacterPath_SameArchetype"));
endif
