ExprLine.OrigStr:
if ( CharacterCreation_IsCostumeOnly() > 0 )
	CharacterCreation_InitCostume();
endif

CharacterCreation_ClearCostume();
if (not CharacterCreation_AssignDefaultCostumes())
	CharacterCreation_BuildPlainCostumes();
endif

if (Gen("NemesisGender_Root").Var[SelectedGender].String = "")
	CostumeCreator_SetSkeleton(Self.Var[Gender].String);
	GenSetString(Gen("NemesisGender_Root"), "SelectedGender", Self.Var[Gender].String);
elif (Gen("NemesisGender_Root").Var[SelectedGender].String = "Male")
	CostumeCreator_SetSkeleton("Female");
	GenSetString(Gen("NemesisGender_Root"), "SelectedGender", "Female");
else
	CostumeCreator_SetSkeleton("Male");
	GenSetString(Gen("NemesisGender_Root"), "SelectedGender", "Male");
endif

CharacterCreation_ChooseCachedCostume();
CostumeCreator_SetAllMaterialLink(CostumeColorLink_None);
CharacterCreation_ForceCostumeStance(1);

GenSendMessage(Gen("CharacterEditor_Root"), "ResetUndo");
