ExprLine.OrigStr:
If (CostumeCreator_TailorReady())
	GenSetValue(Gen("Tailor_Main_Window"), "ListIndex", GenInstanceNumber);
	GenSetValue(Gen("Tailor_Main_Window"), "ListType", 0);
	GenSendMessage(Gen("Tailor_Main_Window"), "CopyCostume");
	GenSendMessage(Gen("Tailor_Main_Window"), "StartEdit");
EndIf

DeclareType("GenInstanceData", "PlayerCostume");
if (GenInstanceData)
	if (InState(MouseOver))
		GenSetString(Gen("Tailor_Main_CostumeList_Instance_Name"), "MouseOverName", GenInstanceData.Name);
	endif
	if (InState(Selected))
		GenSetString(Gen("Tailor_Main_CostumeList_Instance_Name"), "SelectedName", GenInstanceData.Name);
	endif
elif (not GenInstanceData)
	if (InState(MouseOver))
		GenSetString(Gen("Tailor_Main_CostumeList_Instance_Name"), "MouseOverName", Translate("PlayerStatus_Costume_Unused") );
	endif
	if (InState(Selected))
		GenSetString(Gen("Tailor_Main_CostumeList_Instance_Name"), "SelectedName", Translate("PlayerStatus_Costume_Unused") );
	endif
endif
