DeclareType("GenInstanceData", "UITrayElem");
if (GenHasVar(Self.Parent, "InventoryBag") and GenInstanceData.Slot >= 0)
	if (((ItemGetDefFlags(InventoryKeyGetItem(DragDropData.TextPayload)) & ItemDefFlagBindOnEquip)
		and (not ItemGetFlags(InventoryKeyGetItem(DragDropData.TextPayload)) & ItemFlagBound)))
		GenSetString(Gen("Inventory_BindConfirmDialog"), "SourceInventoryKey", DragDropData.TextPayload);
		GenSetValue(Gen("Inventory_BindConfirmDialog"), "DestInventoryBag", Self.Parent.Var[InventoryBag].Int);
		GenSetValue(Gen("Inventory_BindConfirmDialog"), "DestInventorySlot", GenInstanceData.Slot);
		GenSendMessage(Gen("Inventory_BindConfirmDialog"), "Show");
	else
		InventoryKeyMove(DragDropData.TextPayload, Self.Parent.Var[InventoryBag].Int, GenInstanceData.Slot, -1);
	endif
endif

