ExprLine.OrigStr:
DeclareType("GenInstanceData", "InventorySlot");
if ((not GenInstanceData.pItem) and
	(ItemGetFlags(InventoryKeyGetItem(DragDropData.TextPayload)) & ItemFlagBound) = 0 and
	(ItemGetDefFlags(InventoryKeyGetItem(DragDropData.TextPayload)) & ItemDefFlagBindOnEquip) and
	(ItemGetDefType(InventoryKeyGetItem(DragDropData.TextPayload))) = "Bag")
	GenSetString(Gen("Inventory_BindConfirmDialog"), "SourceInventoryKey", DragDropData.TextPayload);
	GenSetValue(Gen("Inventory_BindConfirmDialog"), "DestInventoryBag", Self.Parent.Var[BagID].Int);
	GenSetValue(Gen("Inventory_BindConfirmDialog"), "DestInventorySlot", GenInstanceNumber);
	GenSendMessage(Gen("Inventory_BindConfirmDialog"), "Show");
elif (InputKeyState(K_SHIFT) and InventoryKeyCount(DragDropData.TextPayload) > 1)
	GenSetString(Gen("CountSlider"), "ItemName", GenGetItemName(InventoryKeyGetItem(DragDropData.TextPayload)));
	GenSetString(Gen("CountSlider"), "InventorySourceKey", DragDropData.TextPayload);
	GenSetString(Gen("CountSlider"), "InventoryDestKey", InventoryIndexGetKey(Player, Self.Parent.Var[BagID].Int, -1));
	GenSendMessage(Gen("CountSlider"), "Move");
else
	InventoryKeyMove(DragDropData.TextPayload, Self.Parent.Var[BagID].Int, StringToInt(GenInstanceData.pchName), -1);
endif

DeclareType("GenInstanceData", "InventorySlot");
if (GenInstanceData.pItem)
	GenDragStringStart(
		Self,
		"InventorySlot",
		InventorySlotGetKey(Player, Self.Parent.Var[BagID].Int, GenInstanceData),
		InventorySlotGetIcon(GenInstanceData),
		MouseLeft);
endif

GenSetValue(Gen("Inventory_PlayerBag_Root"), "BagID", BagIDPlayerBag1 + StringToInt(GenInstanceData.pchName));

if (not GenWindow("Inventory_PlayerBag_Root", 0) = Gen("Inventory_PlayerBag_Root"))
	if (GenGetPersistedInt("BagWindow_0", 0) = Gen("Inventory_PlayerBag_Root").Var[BagID].Int)
		GenSetValue(Gen("Inventory_PlayerBag_Root"), "BagID", 0);
		GenSendMessage(GenWindow("Inventory_PlayerBag_Root", 0), "Toggle")
	endif
endif

if (not GenWindow("Inventory_PlayerBag_Root", 1) = Gen("Inventory_PlayerBag_Root"))
	if (GenGetPersistedInt("BagWindow_1", 0) = Gen("Inventory_PlayerBag_Root").Var[BagID].Int)
		GenSetValue(Gen("Inventory_PlayerBag_Root"), "BagID", 0);
		GenSendMessage(GenWindow("Inventory_PlayerBag_Root", 1), "Toggle")
	endif
endif

if (not GenWindow("Inventory_PlayerBag_Root", 2) = Gen("Inventory_PlayerBag_Root"))
	if (GenGetPersistedInt("BagWindow_2", 0) = Gen("Inventory_PlayerBag_Root").Var[BagID].Int)
		GenSetValue(Gen("Inventory_PlayerBag_Root"), "BagID", 0);
		GenSendMessage(GenWindow("Inventory_PlayerBag_Root", 2), "Toggle")
	endif
endif

if (not GenWindow("Inventory_PlayerBag_Root", 3) = Gen("Inventory_PlayerBag_Root"))
	if (GenGetPersistedInt("BagWindow_3", 0) = Gen("Inventory_PlayerBag_Root").Var[BagID].Int)
		GenSetValue(Gen("Inventory_PlayerBag_Root"), "BagID", 0);
		GenSendMessage(GenWindow("Inventory_PlayerBag_Root", 3), "Toggle")
	endif
endif

if (Gen("Inventory_PlayerBag_Root").Var[BagID].Int)
	GenSendMessage(Gen("Inventory_PlayerBag_Root"), "Show");
endif
