ExprLine.OrigStr:
GenSetValue(Gen("Chat_Root"), "OpenX", MouseX);
GenSetValue(Gen("Chat_Root"), "OpenY", MouseY);
if (InputKeyState(K_CTRL))
	GenSetString(Gen("Chat_Root"), "CopyTab", DragDropData.TextPayload);
	GenSendMessage(Gen("Chat_Root"), "CreateWindowFromTabCopy");
else
	GenSetString(Gen("Chat_Root"), "MoveTab", DragDropData.TextPayload);
	GenSendMessage(Gen("Chat_Root"), "CreateWindowFromTab");
endif

if ( EntGetLevelExp(Player) > 5 AND GenIntVar(Gen("Root"), "ShowedLandingPage") = 0 )
	GenSendMessage(Gen("Landing_Root"), "Show");
	GenSetValue(Gen("Root"), "ShowedLandingPage", GenIntVar(Gen("Root"), "ShowedLandingPage") + 1);
	GenTimerPause(Self, "ShowLandingPage", True);
endif

if ( queue_ReadyQueueGetMapType(Player) = ZoneMapType_PvP )
	GenSendMessage( Gen("QueueAlert"), "Show");
elif ( queue_ReadyQueueGetMapType(Player) = ZoneMapType_QPvE )
	if ( queue_IsPrivate( queue_GetReadyName(Player,""), queue_GetReadyInstance(Player,"") ) ) 
		GenSendMessage( Gen("Alert_JoinPrivate"), "Show");
	else
		GenSendMessage( Gen("Alert_JoinAlert"), "Show"); 
	endif
endif

