ExprLine.OrigStr:
if (Self.Var[Mine].Int)
	ConsolePrint("Resetting " + IntToString(GenInstanceNumber) + " as a mine");
	GenSetValue(Self, "Mine", 0);
	if (Gen("Minesweeper_Root").Var[PlacedMines].Int > 0)
		GenSetValue(Gen("Minesweeper_Root"), "PlacedMines", Gen("Minesweeper_Root").Var[PlacedMines].Int - 1);
	endif
endif

if (not Self.Var[Mine].Int and Gen("Minesweeper_Root").Var[PlacedMines].Int < Gen("Minesweeper_Root").Var[GameMines].Int)
	ConsolePrint("Made " + IntToString(GenInstanceNumber) + " a mine");
	GenSetValue(Self, "Mine", 1);
	GenSetValue(Gen("Minesweeper_Root"), "PlacedMines", Gen("Minesweeper_Root").Var[PlacedMines].Int + 1);
endif

if (Self.Var[Nearby].Int > -1 and Gen("Minesweeper_Root").Var[PlacedMines].Int = Gen("Minesweeper_Root").Var[GameMines].Int)
	if (Self.Var[Mine].Int)
		if ((not Gen("Minesweeper_Root").Var[GameActive].Int and not InState(User)) or InState(Selected))
			# This is a mine
			GenSetState(Self, User3, 1);
			if (Gen("Minesweeper_Root").Var[GameActive].Int)
				# This killed the player
				GenSetValue(Gen("Minesweeper_Root"), "GameActive", 0);
				GenSendMessage(Gen("Minesweeper_Timer"), "Stop");
				GenSetState(Gen("Minesweeper_EndGameState"), User1, 1);
			endif
		endif
	elif (InState(User) and not Gen("Minesweeper_Root").Var[GameActive].Int and not Self.Var[Mine].Int)
		# This was marked incorrectly
		GenSetState(Self, User, 0);
		GenSetState(Self, User2, 1);
		if (Gen("Minesweeper_Root").Var[GameActive].Int)
			GenSendMessage(Gen("Minesweeper_Timer"), "Start");
		endif
	elif (Self.Var[Nearby].Int = 0)
		# Reveal self & nearby
		GenSetState(Self, User4, 1);
		if (Gen("Minesweeper_Root").Var[GameActive].Int)
			if (GenInstanceRow - 1 >= 0)
				if (GenInstanceColumn - 1 >= 0)
					GenSetState(GenLayoutBoxGetInstance(Self.Parent, (GenInstanceRow - 1) * GenInstanceColumnCount + (GenInstanceColumn - 1)), Selected, 1);
				endif
				if (GenInstanceColumn + 1 < GenInstanceColumnCount)
					GenSetState(GenLayoutBoxGetInstance(Self.Parent, (GenInstanceRow - 1) * GenInstanceColumnCount + (GenInstanceColumn + 1)), Selected, 1);
				endif
				GenSetState(GenLayoutBoxGetInstance(Self.Parent, (GenInstanceRow - 1) * GenInstanceColumnCount + (GenInstanceColumn + 0)), Selected, 1);
			endif
			if (GenInstanceRow + 1 < GenInstanceRowCount)
				if (GenInstanceColumn - 1 >= 0)
					GenSetState(GenLayoutBoxGetInstance(Self.Parent, (GenInstanceRow + 1) * GenInstanceColumnCount + (GenInstanceColumn - 1)), Selected, 1);
				endif
				if (GenInstanceColumn + 1 < GenInstanceColumnCount)
					GenSetState(GenLayoutBoxGetInstance(Self.Parent, (GenInstanceRow + 1) * GenInstanceColumnCount + (GenInstanceColumn + 1)), Selected, 1);
				endif
				GenSetState(GenLayoutBoxGetInstance(Self.Parent, (GenInstanceRow + 1) * GenInstanceColumnCount + (GenInstanceColumn + 0)), Selected, 1);
			endif
			if (GenInstanceColumn - 1 >= 0)
				GenSetState(GenLayoutBoxGetInstance(Self.Parent, (GenInstanceRow + 0) * GenInstanceColumnCount + (GenInstanceColumn - 1)), Selected, 1);
			endif
			if (GenInstanceColumn + 1 < GenInstanceColumnCount)
				GenSetState(GenLayoutBoxGetInstance(Self.Parent, (GenInstanceRow + 0) * GenInstanceColumnCount + (GenInstanceColumn + 1)), Selected, 1);
			endif

			GenSendMessage(Gen("Minesweeper_Timer"), "Start");
		endif
	elif (Self.Var[Nearby].Int > 0)
		# Reveal self
		GenSetState(Self, User4, 1);
		if (Gen("Minesweeper_Root").Var[GameActive].Int)
			GenSendMessage(Gen("Minesweeper_Timer"), "Start");
		endif
	endif
endif

# Count mines in nearby tiles
GenSetValue(Self, "Nearby", 0);
if (GenInstanceRow - 1 >= 0)
	if (GenInstanceColumn - 1 >= 0)
		GenSetValue(Self, "Nearby", Self.Var[Nearby].Int + GenLayoutBoxGetInstance(Self.Parent, (GenInstanceRow - 1) * GenInstanceColumnCount + (GenInstanceColumn - 1)).Var[Mine].Int);
	endif
	if (GenInstanceColumn + 1 < GenInstanceColumnCount)
		GenSetValue(Self, "Nearby", Self.Var[Nearby].Int + GenLayoutBoxGetInstance(Self.Parent, (GenInstanceRow - 1) * GenInstanceColumnCount + (GenInstanceColumn + 1)).Var[Mine].Int);
	endif
	GenSetValue(Self, "Nearby", Self.Var[Nearby].Int + GenLayoutBoxGetInstance(Self.Parent, (GenInstanceRow - 1) * GenInstanceColumnCount + (GenInstanceColumn + 0)).Var[Mine].Int);
endif
if (GenInstanceRow + 1 < GenInstanceRowCount)
	if (GenInstanceColumn - 1 >= 0)
		GenSetValue(Self, "Nearby", Self.Var[Nearby].Int + GenLayoutBoxGetInstance(Self.Parent, (GenInstanceRow + 1) * GenInstanceColumnCount + (GenInstanceColumn - 1)).Var[Mine].Int);
	endif
	if (GenInstanceColumn + 1 < GenInstanceColumnCount)
		GenSetValue(Self, "Nearby", Self.Var[Nearby].Int + GenLayoutBoxGetInstance(Self.Parent, (GenInstanceRow + 1) * GenInstanceColumnCount + (GenInstanceColumn + 1)).Var[Mine].Int);
	endif
	GenSetValue(Self, "Nearby", Self.Var[Nearby].Int + GenLayoutBoxGetInstance(Self.Parent, (GenInstanceRow + 1) * GenInstanceColumnCount + (GenInstanceColumn + 0)).Var[Mine].Int);
endif
if (GenInstanceColumn - 1 >= 0)
	GenSetValue(Self, "Nearby", Self.Var[Nearby].Int + GenLayoutBoxGetInstance(Self.Parent, (GenInstanceRow + 0) * GenInstanceColumnCount + (GenInstanceColumn - 1)).Var[Mine].Int);
endif
if (GenInstanceColumn + 1 < GenInstanceColumnCount)
	GenSetValue(Self, "Nearby", Self.Var[Nearby].Int + GenLayoutBoxGetInstance(Self.Parent, (GenInstanceRow + 0) * GenInstanceColumnCount + (GenInstanceColumn + 1)).Var[Mine].Int);
endif

# Reveal this tile if selected or the game is not active anymore
if (Gen("Minesweeper_Root").Var[PlacedMines].Int = Gen("Minesweeper_Root").Var[GameMines].Int)
	if (InState(Selected) or not Gen("Minesweeper_Root").Var[GameActive].Int)
		GenSendMessage(Self, "Reveal");
	endif
endif
