ExprLine.OrigStr:
if (not MapIcon.Entity = 0)
	GenSetValue(Gen("EntityContextMenu"), "EntRef", MapIcon.Entity);
	GenSendMessage(Gen("EntityContextMenu"), "Popup");
endif

if (not MapIcon.Mission = "")
	GenSetString(Gen("Map_ContextMenu"), "Title", MapIcon.Label);
	GenSetString(Gen("Map_ContextMenu"), "Mission", MapIcon.Mission);
	GenSendMessage(Gen("Map_ContextMenu"), "Popup");
endif
