ExprLine.OrigStr:
DeclareType("GenInstanceData", "InventorySlot");
if (InState(ItemDraggable) and InventorySlotCount(GenInstanceData))
	GenDragStringStart(
		Self,
		"InventorySlot",
		InventorySlotGetContKey(Player, ChooseEntity(Self.Parent.Var[SharedBankEnt].Int, EntGetSharedBankEnt(Player), Player), Self.Parent.Var[BagID].Int, GenInstanceData.pchName),
		InventorySlotGetIcon(GenInstanceData),
		MouseLeft);
endif

DeclareType("GenInstanceData", "InventorySlot");
if (InputKeyState(K_SHIFT) and InventoryKeyCount(DragDropData.TextPayload) > 1)
	GenSetString(Gen("CountSlider"), "ItemName", GenGetItemName(InventoryKeyGetItem(DragDropData.TextPayload)));
	GenSetString(Gen("CountSlider"), "InventorySourceKey", DragDropData.TextPayload);
	GenSetString(Gen("CountSlider"), "InventoryDestKey", InventorySlotGetContKey(Player, ChooseEntity(Self.Parent.Var[SharedBankEnt].Int, EntGetSharedBankEnt(Player), Player), Self.Parent.Var[BagID].Int, GenInstanceData.pchName));
	GenSetValue(Gen("CountSlider"), "Count", InventoryKeyCount(DragDropData.TextPayload));
	GenSendMessage(Gen("CountSlider"), "Move");
else
	# If the item is coming from an equip slot, need to do some convoluted checking for causing binding
	if ((InventoryKeyGetBagNumber(DragDropData.TextPayload) = BagIDOffense or
			InventoryKeyGetBagNumber(DragDropData.TextPayload) = BagIDDefense or
			InventoryKeyGetBagNumber(DragDropData.TextPayload) = BagIDUtility) and
			(ItemGetDefFlags(GenInstanceData.pItem) & ItemDefFlagBindOnEquip) and
			(not ItemGetFlags(GenInstanceData.pItem) & ItemFlagBound))
		GenSetString(Gen("Inventory_BindConfirmDialog"), "SourceInventoryKey", InventorySlotGetContKey(Player, ChooseEntity(Self.Parent.Var[SharedBankEnt].Int, EntGetSharedBankEnt(Player), Player), Self.Parent.Var[BagID].Int, GenInstanceData.pchName));
		GenSetValue(Gen("Inventory_BindConfirmDialog"), "DestInventoryBag", InventoryKeyGetBagNumber(DragDropData.TextPayload));
		GenSetValue(Gen("Inventory_BindConfirmDialog"), "DestInventorySlot", InventoryKeyGetSlotNumber(DragDropData.TextPayload));
		GenSendMessage(Gen("Inventory_BindConfirmDialog"), "Show");
	else
		InventoryKeyMoveKey(DragDropData.TextPayload, InventoryIndexGetContKey(Player, ChooseEntity(Self.Parent.Var[SharedBankEnt].Int, EntGetSharedBankEnt(Player), Player), Self.Parent.Var[BagID].Int, StringToInt(GenInstanceData.pchName)), -1);
	endif
endif

DeclareType("GenInstanceData", "InventorySlot");
if (GemSlotUI_IsItemAttached(Gen("GemSlot_Window"), DragDropData.IntPayload))
	GenSetValue(Gen("GemSlot_UnslotDialog"), "GemIndex", DragDropData.IntPayload);
	GenSetString(Gen("GemSlot_UnslotDialog"), "TargetSlot", InventorySlotGetContKey(Player, ChooseEntity(Self.Parent.Var[SharedBankEnt].Int, EntGetSharedBankEnt(Player), Player), Self.Parent.Var[BagID].Int, GenInstanceData.pchName));
	GenSendMessage(Gen("GemSlot_UnslotDialog"), "Show");
else
	GemSlotUI_RemoveGemFromItem(Gen("GemSlot_Window"), InventorySlotGetContKey(Player, ChooseEntity(Self.Parent.Var[SharedBankEnt].Int, EntGetSharedBankEnt(Player), Player), Self.Parent.Var[BagID].Int, GenInstanceData.pchName), DragDropData.IntPayload, "");
endif


DeclareType("GenInstanceData", "InventorySlot");
if (GenInstanceData)
	ItemIsNew(Player, GenInstanceData.pItem)
else 
	0
endif

ConditionBlock._:
(
  str "GenData"
  str "Item"
)
call "Declaretype"
;
(
  static GenData
)
call "Gemslotui_Getnumberofgemslots"

ExprLine.OrigStr:
DeclareType("GenInstanceData", "InventorySlot");
DeclareType("GenData", "Item");
GenSetString(Gen("CountSlider"), "ItemName", InventorySlotGetDisplayName(GenInstanceData));
GenSetValue(Gen("CountSlider"), "BagID", Self.Parent.Var[BagID].Int);
GenSetValue(Gen("CountSlider"), "SlotID", StringToInt(GenInstanceData.pchName));
GenSetValue(Gen("CountSlider"), "Count", InventorySlotGetCount(GenInstanceData));
if (GenInState(Gen("GemSlot_Window"), Visible) and GemSlotUI_CanPlaceGemAnywhere(GemSlotUI_GetItem(Gen("GemSlot_Window")), GenData))
	GemSlotUI_AttachGemToItemAnywhere(Gen("GemSlot_Window"),
		InventorySlotGetContKey(
				Player,
				ChooseEntity(Self.Parent.Var[SharedBankEnt].Int, EntGetSharedBankEnt(Player), Player),
				Self.Parent.Var[BagID].Int,
				GenInstanceData.pchName));

elif ((GenInState(Gen("GemSlot_Window"), Visible) or InputKeyState(K_LCONTROL)) and GemSlotUI_GetNumberOfGemSlots(GenData))
	GenSetString(Gen("GemSlot_Window"), "StartupKey",
		InventorySlotGetContKey(
			Player,
			ChooseEntity(Self.Parent.Var[SharedBankEnt].Int, EntGetSharedBankEnt(Player), Player),
			Self.Parent.Var[BagID].Int,
			GenInstanceData.pchName));
	GenSendMessage(Gen("GemSlot_Window"), "Show");

elif ((GenInState(Gen("ItemSmash_Window"), Visible) or InputKeyState(K_LCONTROL)) and ItemType(GenData) = ItemTypeGem)
	GenSetString(Gen("ItemSmash_Window"), "StartupKey",
		InventorySlotGetContKey(
			Player,
			ChooseEntity(Self.Parent.Var[SharedBankEnt].Int, EntGetSharedBankEnt(Player), Player),
			Self.Parent.Var[BagID].Int,
			GenInstanceData.pchName));
	GenSendMessage(Gen("ItemSmash_Window"), "Show");

elif (InputKeyState(K_LCONTROL) and ItemType(GenData) = ItemTypeComponent)
	GenAddConfirmDialog(Translate("Message_ComponentToGemTitle"), Translate("Message_ComponentToGemBody"), Gen("DummyInventorythingy"));

elif (GenInState(Gen("Auction_NewAuction_AddItem"), Visible))
	GenSendMessage(Gen("CountSlider"), "Auction");

elif (GenInState(Gen("Trade_Root"), Visible))
	GenSendMessage(Gen("CountSlider"), "Trade");

elif (GenInState(Gen("Store"), Visible) and Gen("Store").Var[StoreState].String = "Sell")
	GenSendMessage(Gen("CountSlider"), "Sell");

elif (GenInState(Gen("Bank_Root"), Visible) and Self.Parent.Var[BagID].Int = Gen("Inventory_Bank_Bag").Var[BagID].Int)
	GenSetString(Gen("CountSlider"), "InventorySourceKey", InventorySlotGetContKey(Player, ChooseEntity(Self.Parent.Var[SharedBankEnt].Int, EntGetSharedBankEnt(Player), Player), Self.Parent.Var[BagID].Int, GenInstanceData.pchName));
	if (GenInState(Gen("Inventory_Inventory_Bag"), Visible))
		GenSetString(Gen("CountSlider"), "InventoryDestKey", InventoryIndexGetKey(Player, Gen("Inventory_Inventory_Bag").Var[BagID].Int, -1));
	else
		GenSetString(Gen("CountSlider"), "InventoryDestKey", InventoryIndexGetKey(Player, BagIDInventory, -1));
	endif
	GenSendMessage(Gen("CountSlider"), "Move");
elif (GenInState(Gen("Bank_Root"), Visible))
	GenSetString(Gen("CountSlider"), "InventorySourceKey", InventorySlotGetContKey(Player, ChooseEntity(Self.Parent.Var[SharedBankEnt].Int, EntGetSharedBankEnt(Player), Player), Self.Parent.Var[BagID].Int, GenInstanceData.pchName));
	GenSetString(Gen("CountSlider"), "InventoryDestKey", InventoryIndexGetKey(Player, Gen("Inventory_Bank_Bag").Var[BagID].Int, -1));
	GenSendMessage(Gen("CountSlider"), "Move");

elif (GenInState(Gen("Guildbank_Root"), Visible) and Self.Parent.Var[BagID].Int = Gen("Guildbank_Bag").Var[BagID].Int)
	GenSetString(Gen("CountSlider"), "InventorySourceKey", InventorySlotGetContKey(Player, ChooseEntity(Self.Parent.Var[SharedBankEnt].Int, EntGetSharedBankEnt(Player), Player), Self.Parent.Var[BagID].Int, GenInstanceData.pchName));
	if (GenInState(Gen("Inventory_Inventory_Bag"), Visible))
		GenSetString(Gen("CountSlider"), "InventoryDestKey", InventoryIndexGetKey(Player, Gen("Inventory_Inventory_Bag").Var[BagID].Int, -1));
	else
		GenSetString(Gen("CountSlider"), "InventoryDestKey", InventoryIndexGetKey(Player, BagIDInventory, -1));
	endif
	GenSendMessage(Gen("CountSlider"), "Move");
elif (GenInState(Gen("Guildbank_Root"), Visible))
	GenSetString(Gen("CountSlider"), "InventorySourceKey", InventorySlotGetContKey(Player, ChooseEntity(Self.Parent.Var[SharedBankEnt].Int, EntGetSharedBankEnt(Player), Player), Self.Parent.Var[BagID].Int, GenInstanceData.pchName));
	GenSetString(Gen("CountSlider"), "InventoryDestKey", InventoryIndexGetKey(Player, Gen("Guildbank_Bag").Var[BagID].Int, -1));
	GenSendMessage(Gen("CountSlider"), "Move");

elif (GenInState(Gen("Sharedbank_Root"), Visible) and not Self.Parent.Var[SharedBankEnt].Int)
	GenSetString(Gen("CountSlider"), "InventorySourceKey", InventorySlotGetContKey(Player, Player, Self.Parent.Var[BagID].Int, GenInstanceData.pchName));
	if (GenInState(Gen("Inventory_Inventory_Bag"), Visible))
		GenSetString(Gen("CountSlider"), "InventoryDestKey", InventoryIndexGetContKey(Player, EntGetSharedBankEnt(Player), Gen("Inventory_Inventory_Bag").Var[BagID].Int, -1));
	else
		GenSetString(Gen("CountSlider"), "InventoryDestKey", InventoryIndexGetContKey(Player, EntGetSharedBankEnt(Player), BagIDInventory, -1));
	endif
	GenSendMessage(Gen("CountSlider"), "Move");
elif (GenInState(Gen("Sharedbank_Root"), Visible))
	GenSetString(Gen("CountSlider"), "InventorySourceKey", InventorySlotGetContKey(Player, EntGetSharedBankEnt(Player), Self.Parent.Var[BagID].Int, GenInstanceData.pchName));
	GenSetString(Gen("CountSlider"), "InventoryDestKey", InventoryIndexGetKey(Player, Gen("Sharedbank_Bag").Var[BagID].Int, -1));
	GenSendMessage(Gen("CountSlider"), "Move");

else
	GenSendMessage(Self, "Equip");
endif

DeclareType("GenInstanceData", "InventorySlot");
if (ItemGetDefFlags(GenInstanceData.pItem) & ItemDefFlagBindOnEquip and not ItemGetFlags(GenInstanceData.pItem) & ItemFlagBound)
	GenSetString(Gen("Inventory_BindConfirmDialog"), "SourceInventoryKey", InventorySlotGetContKey(Player, ChooseEntity(Self.Parent.Var[SharedBankEnt].Int, EntGetSharedBankEnt(Player), Player), Self.Parent.Var[BagID].Int, GenInstanceData.pchName));
	GenSetValue(Gen("Inventory_BindConfirmDialog"), "DestInventoryBag", -1);
	GenSetValue(Gen("Inventory_BindConfirmDialog"), "DestInventorySlot", -1);
	GenSendMessage(Gen("Inventory_BindConfirmDialog"), "Show");
else
	if (not ItemPlayerHasCorrectSkillType(Player, GenInstanceData.pItem))
		NotifySend("ItemMoveFailed", MessageFormatItemDef("Inventory_IncorrectSkillType_Prompt", GenGetItemDef(GenInstanceData.pItem)), "", "");
	elif (not ItemPlayerHasCorrectSkillSpecialization(Player, GenInstanceData.pItem))
		NotifySend("ItemMoveFailed", MessageFormatItemDef("Inventory_IncorrectSkillSpecialization_Prompt", GenGetItemDef(GenInstanceData.pItem)), "", "");
	elif (not ItemPlayerHasCorrectSkillLevel(Player, GenInstanceData.pItem))
		NotifySend("ItemMoveFailed", MessageFormatItemDef("Inventory_IncorrectSkillLevel_Prompt", GenGetItemDef(GenInstanceData.pItem)), "", "");
	else
		InventoryIndexEquip(Self.Parent.Var[BagID].Int, StringToInt(GenInstanceData.pchName));
	endif
endif

DeclareType("GenInstanceData", "InventorySlot");
DeclareType("GenData", "Item");
GenSetState(Self, User, GenInState(Gen("GemSlot_Window"), Visible) and GemSlotUI_CanPlaceGemAnywhere(GemSlotUI_GetItem(Gen("GemSlot_Window")), GenData));
GenSetState(Self, ItemUndraggable, GenItemIsTradedByIndex(Player, Self.Parent.Var[BagID].Int, StringToInt(GenInstanceData.pchName)) and not GenInstanceData.pchName = "");

if (not InState(ItemUndraggable))
	if (GenData)
		if (not InventoryKeyCanTrade(InventorySlotGetContKey(Player, ChooseEntity(Self.Parent.Var[SharedBankEnt].Int, EntGetSharedBankEnt(Player), Player), Self.Parent.Var[BagID].Int, GenInstanceData.pchName)))
			GenSetState(Self, ItemUndraggable,
				GenInState(Gen("Auction_Sell_MyAuctionList"), Visible)
				or GenInState(Gen("Trade_Root"), Visible)
				or GenInState(Gen("MailCompose_Root"), Visible)
				or (GenInState(Gen("GuildBank_Bag"), Visible) and not Gen("GuildBank_Bag") = Self.Parent));
		endif
	endif
endif

if (not InState(ItemUndraggable))
	if (GenData and GenInState(Gen("GemSlot_Window"), Visible))
		GenSetState(Self, ItemUndraggable, 
			GemSlotUI_ItemInUse(Gen("GemSlot_Window"), GenData)
			or (not InState(User) and not GemSlotUI_GetNumberOfGemSlots(GenData)));
	endif
endif

if (not InState(ItemUndraggable))
	GenSetState(Self, ItemUndraggable,
		GenData
		and GenInState(Gen("ItemSmash_Window"), Visible)
		and (GenData = ItemUpgradeUI_GetSourceItem(Gen("ItemSmash_Window"))
			or GenData = ItemUpgradeUI_GetModItem(Gen("ItemSmash_Window"))));
endif

GenSetState(Self, ItemDraggable, not InState(ItemUndraggable));
