ExprLine.OrigStr:
if (RowData.mission)
	GenSetEarlyOverrideString(Self, "Font",
		"Mission_Tracker_Con_"
		+ GetConForMissionAndLevelDeltaWithSeparator(RowData.mission, RowData.LevelDelta, ""));
endif

GenSetString(Self, "MissionTitle", GetMissionDisplayName(Player, RowData.Mission));

GenSetString(Self, "Level", MissionGetLevelAsString(Player, RowData.Mission));

if (GetMissionDisplayState(RowData.Mission) = "InProgress")
	GenSetString(Self, "TimerValue", GetMissionTimerString(RowData.Mission));
else
	GenSetString(Self, "TimerValue", "");
endif

if (Self.Var[TimerValue].String = "")
	GenSetString(Self, "TimerString", "");
else
	GenSetString(Self, "TimerString", "<font style=Game_Header>" + Self.Var[TimerValue].String + "&nbsp;-&nbsp;</font>");
endif

if (MissionGetTeamSize(RowData.Mission) > 0)
	GenSetString(Self, "TeamSizeValue", MissionGetTeamSizeString(RowData.Mission));
	GenSetString(Self, "TeamSize", " (" + Self.Var[TeamSizeValue].String + ")");
else
	GenSetString(Self, "TeamSizeValue", "");
	GenSetString(Self, "TeamSize", "");
endif

Self.Var[TimerString].String 
+ Self.Var[MissionTitle].String
+ Self.Var[TeamSize].String

GenSetString(Self, "MapName", GetMapNameFromMission(RowData.Mission));

if (GetMissionListNodeType(RowData) = "ReturnString")
	GenSetString(Self, "Description", GetMissionReturnString(Player, RowData.Mission));
	GenSetString(Self, "Count", "");
	GenSetString(Self, "TimerValue", "");
	if (RowData.ShadowMission)
		GenSetString(Self, "ColorStringBody", "color=Secondary_Mission_Success");
	else 
		GenSetString(Self, "ColorStringBody", "color=success");
	endif
elif (GetMissionListNodeType(RowData) = "FailedReturnString")
	GenSetString(Self, "Description", GetMissionFailedReturnString(Player, RowData.Mission));
	GenSetString(Self, "Count", "");
	GenSetString(Self, "TimerValue", "");
	GenSetString(Self, "ColorStringBody", "color=#ffffff");
elif (GetMissionListNodeType(RowData) = "MapString")
	GenSetString(Self, "Description", Translate("HUD_MissionHelper_GoToMap"));
	GenSetString(Self, "Count", "");
	GenSetString(Self, "TimerValue", GetMissionTimerString(RowData.Mission));
	GenSetString(Self, "ColorStringBody", "color=#ffffff");
elif (GetMissionListNodeType(RowData) = "UIString")
	GenSetString(Self, "Description", GetMissionUIString(Player, RowData.Mission));
	if (GetMissionCountString(RowData.Mission) = "")
		GenSetString(Self, "Count", "");
	else
		GenSetString(Self, "Count", " " + GetMissionCountString(RowData.Mission));
	endif

	if (GetMissionDisplayState(RowData.Mission) = "InProgress")
		if (NOT MissionHasDisplayName(RowData.Mission))
			GenSetString(Self, "TimerValue", GetMissionTimerString(RowData.Mission));
		endif
		GenSetString(Self, "ColorStringBody", "color=#ffffff");
	elif (GetMissionDisplayState(RowData.Mission) = "Succeeded")
		GenSetString(Self, "TimerValue", "");
		if (RowData.ShadowMission)
			GenSetString(Self, "ColorStringBody", "color=Secondary_Mission_Success");
		else 
			GenSetString(Self, "ColorStringBody", "color=success");
		endif
	else
		GenSetString(Self, "TimerValue", "");
		if (RowData.ShadowMission)
			GenSetString(Self, "ColorStringBody", "color=Secondary_Mission_Failure");
		else 
			GenSetString(Self, "ColorStringBody", "color=failure");
		endif
	endif
else
	GenSetString(Self, "Description", GetMissionUIString(Player, RowData.Mission));
	if (GetMissionCountString(RowData.Mission) = "")
		GenSetString(Self, "Count", "");
	else
		GenSetString(Self, "Count", " " + GetMissionCountString(RowData.Mission));
	endif
	GenSetString(Self, "TimerValue", GetMissionTimerString(RowData.Mission));
	GenSetString(Self, "ColorStringBody", "color=#ffffff");
endif

if (Self.Var[TimerValue].String = "")
	GenSetString(Self, "TimerString", "");
else
	GenSetString(Self, "TimerString", "<font style=Game_Highlight_Small>" + Self.Var[TimerValue].String + "&nbsp;-&nbsp;</font>");
endif

if (GetMissionListNodeType(RowData) = "MapString")
	"<table><tr><td width=10></td><td valign=center><img src=Tooltip_Check_Box_Bullet_Point align=left/></td><td width=100% valign=center border=0><font style=Game_HUD_Small " + Self.Var[ColorStringBody].String + ">" 
	+ Self.Var[TimerString].String
	+ Self.Var[Description].String
	+ Self.Var[Count].String 
	+ "</font></td></tr></table>"
else
	"<table><tr><td valign=center><img src=Tooltip_Check_Box_Bullet_Point align=left/></td><td width=100% valign=center border=0><font style=Game_HUD_Small " + Self.Var[ColorStringBody].String + ">" 
	+ Self.Var[TimerString].String
	+ Self.Var[Description].String
	+ Self.Var[Count].String 
	+ "</font></td></tr></table>"
endif

DeclareType("RowData", "MissionListNode");
if(GetMissionListNodeType(RowData) = "DisplayName" and CanChangePrimaryMission(Player))
	if(IsPrimaryMission(Player, RowData.Mission.MissionName))
		GenRunCommand(Self, "PrimaryMission " + RowData.Mission.MissionName + " 0");
	else
		GenRunCommand(Self, "PrimaryMission " + RowData.Mission.MissionName + " 1");
	endif
endif

DeclareType("RowData", "MissionListNode");

if (RowData.mission)
	GenSetEarlyOverrideString(Self, "Font",
		"Mission_Tracker_Con_Equal");
endif

GenSetValue(Self, "MissionNumber", GetMissionListNodeMissionNumber(RowData));

FormatInt(Self.Var[MissionNumber].Int)
