ExprLine.OrigStr:
DeclareType("RowData", "StoreItemInfo");
if (Self.Timer[Cooldown].Paused)
	if (InState(MicroTransacted))
		if (not RowData.MicroTransactionID = 0)
			GenSetValue(Gen("MTConfirm"), "ID", RowData.MicroTransactionID);
			GenSetString(Gen("MTConfirm"), "Category", MicroTransProduct(RowData.MicroTransactionID).Category);
			GenSetString(Gen("MTConfirm"), "Name", MicroTransProduct(RowData.MicroTransactionID).Name);
			GenSetString(Gen("MTConfirm"), "Cost", FormatInt(MicroTransProduct(RowData.MicroTransactionID).Price));
			GenSetString(Gen("MTConfirm"), "Abbrev", Translate("Transactions.CP_Abbrev"));
			GenSendMessage(Gen("MTConfirm"), "Show");
		endif
	elif (Gen("Store").Vars[StoreState].String = "Buy")
		GenSetValue(Gen("Store"), "BuyCount", 1);
		if (RowData.MayBuyInBulk > 1)
			GenSetString(Gen("CountSlider"), "ItemName", GenGetItemName(RowData.Item));
			GenSetValue(Gen("CountSlider"), "Count", RowData.MayBuyInBulk);
			GenSendMessage(Gen("CountSlider"), "Buy");
		else
			StoreBuyItem(Self, Player, RowData, 1)
		endif
	elif (Gen("Store").Vars[StoreState].String = "Sell")
		GenSetString(Gen("CountSlider"), "ItemName", GenGetItemName(RowData.Item));
		GenSetValue(Gen("CountSlider"), "BagID", RowData.BagID);
		GenSetValue(Gen("CountSlider"), "SlotID", RowData.Slot);
		GenSetValue(Gen("CountSlider"), "Count", RowData.Item.Count);
		GenSendMessage(Gen("CountSlider"), "Sell");
	elif (Gen("Store").Vars[StoreState].String = "Buyback")
		StoreBuybackItem(Self, Player, RowData, 1)
	endif
	GenTimerSet(Self, "Cooldown", 0);
	GenTimerPause(Self, "Cooldown", False);
endif

			DeclareType("RowData", "StoreItemInfo");

			if (not RowData.RequiredMicroTransaction = "")
				if (RowData.RequiredMicroTransactionID = 0)
					# We don't have the micro transaction information for this product yet
					GenSendMessage(Self.Parent, "MicroTransactionsRequest");
				endif
			endif

			if (RowData.FailsRequirements = StoreCanBuyError_CostRequirement)
				GenSetString(Self, "PrependText", "<span align=center><font style=Game_HUD_Failure>" + RowData.RequirementsText + "</font></span><br>");
				GenSetString(Self, "RequirementsText", "");
			elif (RowData.FailsRequirements)
				GenSetString(Self, "PrependText", "<span align=center><font style=Game_HUD_Failure>" + RowData.RequirementsText + "</font></span><br>");
				GenSetString(Self, "RequirementsText", "");
#				GenSetString(Self, "RequirementsText", "<font style=Game_HUD_Failure scale=.8>" + RowData.RequirementsText + "</font>");
			else
				GenSetString(Self, "PrependText", "");
				GenSetString(Self, "RequirementsText", RowData.RequirementsText);
			endif

			if (not RowData.MicroTransaction = "")
				# This item is available through the C-Store
				GenSetState(Self, MicroTransacted, True);

				# Fill in standard CP string
				GenSetString(Self, "Value", Translate("Transactions.PriceCP"));

				if (RowData.MicroTransactionID = 0)
					# We don't have the micro transaction information for this product yet
					GenSendMessage(Self.Parent, "MicroTransactionsRequest");
					GenSetString(Self, "Points", "");
					GenSetValue(Self, "MeetsRequirements", False);
					GenSetState(Self, Disabled, True);
				else
					# Fill in the details
					GenSetString(Self, "Points", FormatInt(MicroTransProduct(RowData.MicroTransactionID).Price));
					GenSetString(Self, "ItemName", MicroTransProduct(RowData.MicroTransactionID).Name);
					GenSetString(Self, "ItemTexture", MicroTransProduct(RowData.MicroTransactionID).Icon);

					# Check to see if the player can purchase it
					if (MicroTransProduct(RowData.MicroTransactionID).CannotPurchaseType = MTPurchaseErrorType_None)
						# All is good in the world, and the player can share their wealth
						GenSetValue(Self, "MeetsRequirements", True);
						GenSetState(Self, Disabled, False);

					else
						# Sadly the player can't get this, explain way
						GenSetValue(Self, "MeetsRequirements", False);
						GenSetState(Self, Disabled, True);

						GenSetString(Self, "Value", "<font style=Game_HUD_Failure>" + Translate("Transactions.PriceCP") + "</font>");

						GenSetString(Self, "PrependText", "<span align=center><font style=Game_HUD_Failure>" + MicroTransProduct(RowData.MicroTransactionID).CannotPurchaseReason + "</font></span><br>");
						if (not MicroTransProduct(RowData.MicroTransactionID).CannotPurchaseType = MTPurchaseErrorType_NotEnoughCurrency)
							GenSetString(Self, "RequirementsText", "<font style=Game_HUD_Failure scale=.8>" + MicroTransProduct(RowData.MicroTransactionID).CannotPurchaseReason + "</font>");
						endif
					endif
				endif

			else
				# This is not an item that may be purchased through a micro transaction
				GenSetState(Self, MicroTransacted, False);

				# TODO: incorporate StoreGetItemValueTable(RowData, "", "", "<font color=failure>", "</font>", "Resources")
				if (StoreGetItemValueInCurrency(RowData, "Resources") > 0)
					GenSetString(Self, "Value", InventoryGetNumericString3(StoreGetItemValueInCurrency(RowData, "Resources"), "Inventory_Numeric3_Resources_Right", 100, 100));
				else
					GenSetString(Self, "Value", "");
				endif
				GenSetString(Self, "Value", Self.Var[Value].String + StoreGetItemValueTable(RowData, "", "", "<font style=Game_HUD_Failure>", "</font>", "Resources"));

				if ((not StoreItemIsEnabled(RowData)) or RowData.FailsRequirements)
					GenSetValue(Self, "MeetsRequirements", 0);
					GenSetState(Self, Disabled, True);
				else
					GenSetValue(Self, "MeetsRequirements", 1);
					GenSetState(Self, Disabled, False);
				endif
			endif

			DeclareType("GenData", "Item");

			if (not RowData.MicroTransaction = "")
				GenSetString(Self, "ItemQuality", StaticDefineIntGetKey("ItemQuality", ItemGetQuality(GenData)));
			elif (GenData)
				GenSetString(Self, "ItemTexture", GenGetItemIconTexName(GenData));
				GenSetString(Self, "ItemName", GenGetItemName(GenData));
				GenSetString(Self, "ItemQuality", StaticDefineIntGetKey("ItemQuality", ItemGetQuality(GenData)));
			else
				GenSetString(Self, "ItemTexture", "");
				GenSetString(Self, "ItemName", "");
			endif
