ExprLine.OrigStr:
if (GenListSize(Gen("CurrencyExchange_OpenOrders_List")) = CurrencyExchange_GetMaxiumOrders())
	GenSetString(Self, "Error", Translate("CurrencyExchange_Error_TooManyOrders"));
elif (GenInState(Gen("CurrencyExchange_SellCreate_CrypticPointsInput"), User1) or GenInState(Gen("CurrencyExchange_BuyCreate_CrypticPointsInput"), User1))
	if ((GenInState(Gen("CurrencyExchange_SellCreate_CrypticPointsInput"), User1) and GenInState(Gen("CurrencyExchange_SellCreate_CrypticPointsInput"), Timer1))
	    or (GenInState(Gen("CurrencyExchange_BuyCreate_CrypticPointsInput"), User1) and GenInState(Gen("CurrencyExchange_BuyCreate_CrypticPointsInput"), Timer1)))
		GenSetString(Self, "Error", MessageFormatInt2("CurrencyExchange_Error_BadCrypticPointCount", CurrencyExchange_GetMinimumQuantity(), CurrencyExchange_GetMaximumQuantity()));
	else
		GenSetString(Self, "Error", Translate("CurrencyExchange_Error_BadCrypticPoints"));
	endif
elif (GenInState(Gen("CurrencyExchange_SellCreate_QuestioniteInput"), User1) or GenInState(Gen("CurrencyExchange_BuyCreate_QuestioniteInput"), User1))
	GenSetString(Self, "Error", Translate("CurrencyExchange_Error_BadQuestionite"));
elif (GenInState(Gen("CurrencyExchange_SellCreate_PriceInput"), User1) or GenInState(Gen("CurrencyExchange_BuyCreate_PriceInput"), User1))
	GenSetString(Self, "Error", MessageFormatInt2("CurrencyExchange_Error_BadPrice", CurrencyExchange_GetMinimumPrice(), CurrencyExchange_GetMaximumPrice()));
elif (InState(Disabled))
	GenSetString(Self, "Error", "Unknown Error");
else
	GenSetString(Self, "Error", "");
endif
