ExprLine.OrigStr:
if (GemSlotUI_IsItemAttached(Self, DragDropData.IntPayload))
	GenSetValue(Gen("GemSlot_UnslotDialog"), "GemIndex", DragDropData.IntPayload);
	GenSetString(Gen("GemSlot_UnslotDialog"), "TargetSlot", "");
	GenSendMessage(Gen("GemSlot_UnslotDialog"), "Show");
else
	GemSlotUI_RemoveGemFromItem(Self, "", DragDropData.IntPayload, "");
endif


DeclareType("RowData", "Item");
if (InputKeyState(K_LCONTROL))			
	if (GemSlotUI_IsItemAttached(Self, Self.Parent.State.Row))
		GenSetValue(Gen("GemSlot_UnslotDialog"), "GemIndex", Self.Parent.State.Row);
		GenSetString(Gen("GemSlot_UnslotDialog"), "TargetSlot", "");
		GenSendMessage(Gen("GemSlot_UnslotDialog"), "Show");
	else
		GemSlotUI_RemoveGemFromItem(Self, "", Self.Parent.State.Row, "");
	endif
endif

DeclareType("RowData", "Item");
if (RowData)
	GenDragIntStart(Self, "Gem", Self.Parent.State.Row, GenGetItemIconTexName(RowData), MouseLeft);
endif

GenSetString(Gen("GemSlot_Dummy_Attach"), "ItemKey", DragDropData.TextPayload);
GenSetValue(Gen("GemSlot_Dummy_Attach"), "GemIndex", Self.State.Row);
if (GemSlotUI_CanPlaceGemInSlot(
		GemSlotUI_GetItem(Self),
		InventoryKeyGetItem(DragDropData.TextPayload),
		Self.State.Row))
	if (GemSlotUI_IsItemAttached(Self, Self.State.Row))
		GenAddConfirmDialog(Translate("GemSlotUI_ConfirmDialog_Header"), Translate("GemSlotUI_ConfirmDialog_SlotGem"), Gen("GemSlot_Dummy_Attach"));
	else
		GenSendMessage(Gen("GemSlot_Dummy_Attach"), "DialogOK");
	endif
endif
