ExprLine.OrigStr:
if (GemSlotUI_IsItemAttached(Self, DragDropData.IntPayload))
	GenSetValue(Gen("GemSlot_UnslotDialog"), "GemIndex", DragDropData.IntPayload);
	GenSetString(Gen("GemSlot_UnslotDialog"), "TargetSlot", "");
	GenSendMessage(Gen("GemSlot_UnslotDialog"), "Show");
else
	GemSlotUI_RemoveGemFromItem(Self, "", DragDropData.IntPayload, "");
endif


DeclareType("RowData", "Item");
if (InputKeyState(K_LCONTROL))			
	if (GemSlotUI_IsItemAttached(Self,  GemSlotUI_GetGemTypeInstanceNumber(Self,Self.Parent.Parent.Var[System].String,GenInstanceNumber)))
		GenSetValue(Gen("GemSlot_UnslotDialog"), "GemIndex",  GemSlotUI_GetGemTypeInstanceNumber(Self,Self.Parent.Parent.Var[System].String,GenInstanceNumber));
		GenSetString(Gen("GemSlot_UnslotDialog"), "TargetSlot", "");
		GenSendMessage(Gen("GemSlot_UnslotDialog"), "Show");
	else
		GemSlotUI_RemoveGemFromItem(Self, "",  GemSlotUI_GetGemTypeInstanceNumber(Self,Self.Parent.Parent.Var[System].String,GenInstanceNumber), "");
	endif
endif

DeclareType("GenData", "Item");
if (GenData)
	GenDragIntStart(Self, "Gem", GemSlotUI_GetGemTypeInstanceNumber(Self,Self.Parent.Parent.Var[System].String,GenInstanceNumber), GenGetItemIconTexName(GenData), MouseLeft);
endif

GenSetString(Gen("GemSlot_Dummy_Attach"), "ItemKey", DragDropData.TextPayload);
GenSetValue(Gen("GemSlot_Dummy_Attach"), "GemIndex",  GemSlotUI_GetGemTypeInstanceNumber(Self,Self.Parent.Var[System].String,GenInstanceNumber));
if (GemSlotUI_CanPlaceGemInSlot(
		GemSlotUI_GetItem(Self),
		InventoryKeyGetItem(DragDropData.TextPayload),
		GemSlotUI_GetGemTypeInstanceNumber(Self,Self.Parent.Var[System].String,GenInstanceNumber)))
	if (GemSlotUI_IsItemAttached(Self, GemSlotUI_GetGemTypeInstanceNumber(Self,Self.Parent.Var[System].String,GenInstanceNumber)))
		GenAddConfirmDialog(Translate("GemSlotUI_ConfirmDialog_Header"), Translate("GemSlotUI_ConfirmDialog_SlotGem"), Gen("GemSlot_Dummy_Attach"));
	else
		GenSendMessage(Gen("GemSlot_Dummy_Attach"), "DialogOK");
	endif
endif

if (GemSlotUI_IsItemAttached(Self, DragDropData.IntPayload))
	GenSetValue(Gen("GemSlot_UnslotDialog"), "GemIndex", DragDropData.IntPayload);
	GenSetString(Gen("GemSlot_UnslotDialog"), "TargetSlot", "");
	GenSendMessage(Gen("GemSlot_UnslotDialog"), "Show");
else
	GemSlotUI_RemoveGemFromItem(Self, "", DragDropData.IntPayload, "");
endif
