ExprLine.OrigStr:

if ( Gen("SharedBank_ModalDialog_Root").Var[Numeric].String = "Resources" )
	if ( Gen("SharedBank_ModalDialog_Root").Var[WindowMode].String = "Deposit" )
		SharedBankAddNumeric(Player, Gen("SharedBank_ModalDialog_Body_ResourcesEntry").Var[Value].Int, "Resources"); 
	else
		SharedBankAddNumeric(Player, Gen("SharedBank_ModalDialog_Body_ResourcesEntry").Var[Value].Int * -1, "Resources"); 
	endif
elif ( Gen("SharedBank_ModalDialog_Root").Var[Numeric].String = "Daily_Mission_Token" )

	if ( Gen("SharedBank_ModalDialog_Root").Var[WindowMode].String = "Deposit" )
		SharedBankAddNumeric(Player, StringToInt(GenGetTextEntryText(Gen("SharedBank_ModalDialog_Body_SalvageEntry_TextEntry"))), "Daily_Mission_Token"); 
	else
		SharedBankAddNumeric(Player, StringToInt(GenGetTextEntryText(Gen("SharedBank_ModalDialog_Body_SalvageEntry_TextEntry"))) * -1, "Daily_Mission_Token"); 
	endif	
endif
GenRemoveModal(Gen("SharedBank_ModalDialog_Root"));


if ( Gen("SharedBank_ModalDialog_Root").Var[Numeric].String = "Resources" )
	if ( Gen("SharedBank_ModalDialog_Root").Var[WindowMode].String = "Deposit" )
		GenSetState(Self, Disabled,  ( Gen("SharedBank_ModalDialog_Body_ResourcesEntry").Var[Value].Int = 0 ) or ( Gen("SharedBank_ModalDialog_Body_ResourcesEntry").Var[Value].Int > InventoryGetNumericValue(Player, "Resources") ) );
	else
		GenSetState(Self, Disabled,  ( Gen("SharedBank_ModalDialog_Body_ResourcesEntry").Var[Value].Int = 0 ) or ( Gen("SharedBank_ModalDialog_Body_ResourcesEntry").Var[Value].Int > InventoryGetNumericValue(EntGetSharedBankEnt(Player), "Resources") ) );
	endif
elif ( Gen("SharedBank_ModalDialog_Root").Var[Numeric].String = "Daily_Mission_Token" )

	if ( Gen("SharedBank_ModalDialog_Root").Var[WindowMode].String = "Deposit" )
		GenSetState(Self, Disabled, ( StringToInt(GenGetTextEntryText(Gen("SharedBank_ModalDialog_Body_SalvageEntry_TextEntry"))) = 0 ) or ( InventoryGetNumericValue(Player, "Daily_Mission_Token") < StringToInt(GenGetTextEntryText(Gen("SharedBank_ModalDialog_Body_SalvageEntry_TextEntry")))));
	else
		GenSetState(Self, Disabled, ( StringToInt(GenGetTextEntryText(Gen("SharedBank_ModalDialog_Body_SalvageEntry_TextEntry"))) = 0 ) or ( InventoryGetNumericValue(EntGetSharedBankEnt(Player), "Daily_Mission_Token") < StringToInt(GenGetTextEntryText(Gen("SharedBank_ModalDialog_Body_SalvageEntry_TextEntry")))));
	endif	
endif
