endif

DeclareType("GenInstanceData", "PowerListNode");
PowersUI_IsPowerNodeOwned(GenInstanceData) and
InState(Gameplay) and not (GenInstanceData.IsGroup or GenInstanceData.IsTree) and EntCanSetPowerHue(Player, GenInstanceData.Power.uiID)

if (not Self.Parent.Var[Hue].Int = 0)
	GenSetEarlyOverrideInt(Self, "Color", HueToRGB(Self.Parent.Var[Hue].Int));
else
	GenSetEarlyOverrideInt(Self, "Color", 125);
endif

if (not Self.Var[Memory].Int = EntGetPowerHue(Player, GenInstanceData.Power.uiID))
	GenSetValue(Self.Parent, "Hue", EntGetPowerHue(Player, GenInstanceData.Power.uiID));
	GenSetValue(Self, "Memory", EntGetPowerHue(Player, GenInstanceData.Power.uiID));
endif

DeclareType("GenInstanceData", "PowerListNode");
if (PowersUI_IsPowerNodeOwned(GenInstanceData) and InState(Gameplay) and GenInstanceData.Power)
	if (EntCanSetPowerEmit(Player, GenInstanceData.Power.uiID))
		GenSetState(Self.Result.InlineChild[PowerEmitButton], User, 1);
		GenButtonClick(Self.Result.InlineChild[PowerEmitButton]);
	endif
