	GenSetString(Gen("OwnedPowers_Description"), "DescriptionText", GenAutoDescPowerDefByName(Player, RowData.Upgrade.Power, "AdvantageMessageKey_Name", "AttribModMessageKey"));
	if (not (InState(User) or InState(User1)))
		if (not RowData.PowerListNode.IsOwned)
			GenAppendString(Gen("OwnedPowers_Description"), "DescriptionText", "<br><br><font style=Game_HUD_Failure>" + Translate("OwnedPowers_Upgrade_Power") + "</font>");
		elif (RowData.Upgrade.Cost > EntityGetPowerTablePoints(Player, "EnhPoints"))
			GenAppendString(Gen("OwnedPowers_Description"), "DescriptionText", "<br><br><font style=Game_HUD_Failure>" + Translate("OwnedPowers_Upgrade_NotEnoughPoints") + "</font>");
		elif (RowData.Upgrade.Cost <= EntityGetPowerTablePoints(Player, "EnhPoints"))
			if (RowData.IsRank)
				GenAppendString(Gen("OwnedPowers_Description"), "DescriptionText", "<br><br><font style=Game_HUD_Failure>" + Translate("OwnedPowers_Upgrade_PreviousRank") + "</font>");
			else
				GenAppendString(Gen("OwnedPowers_Description"), "DescriptionText", "<br><br><font style=Game_HUD_Failure>" + Translate("OwnedPowers_Upgrade_Requirements") + "</font>");
			endif
		endif
	endif
	GenAppendString(Gen("OwnedPowers_Description"), "DescriptionText", "<br>" + GenAutoDescPowerDefByName(Player, RowData.Upgrade.Power, "AdvantageMessageKey_Data", "AttribModMessageKey"));
	GenSetString(Gen("OwnedPowers_Description"), "AdvancedDescriptionText", "");
else
	GenSetValue(Gen("OwnedPowers_Description"), "ShowAdvancedDescriptionButton", True);
	if (PowersUI_UseStatDescription(RowData.PowerListNode))
		GenSetValue(Gen("OwnedPowers_Description"), "ShowAdvancedDescriptionButton", False);
		GenSetString(Gen("OwnedPowers_Description"), "DescriptionText", PowersUI_GetPowerListNodeStatDescription(Player, RowData.PowerListNode, "StatOnly_Name", "StatOnly_DescriptionRow"));
	elif (RowData.PowerListNode.IsOwned)
		GenSetString(Gen("OwnedPowers_Description"), "DescriptionText", PowersUI_GetPowerListNodeDescription(Player, RowData.PowerListNode, "PowersUI_Description_Owned"));
	elif (RowData.PowerListNode.IsAvailable)
		GenSetString(Gen("OwnedPowers_Description"), "DescriptionText", PowersUI_GetPowerListNodeDescription(Player, RowData.PowerListNode, "PowersUI_Description_Available"));
	else
		GenSetString(Gen("OwnedPowers_Description"), "DescriptionText", PowersUI_GetPowerListNodeDescription(Player, RowData.PowerListNode, "PowersUI_Description_Unavailable"));
	endif

	GenSetString(Gen("OwnedPowers_Description"), "AdvancedDescriptionText", GenAutoDescPowerListNode(Player, RowData.PowerListNode, "PowerMessageKey", "AttribModMessageKey"));
