ExprLine.OrigStr:
 			DeclareType("GenInstanceData", "PowerListNode");
 			if (PowersUI_UseStatDescription(GenInstanceData))
 				GenSetString(Gen("PowersUI_Power_Template"), "TooltipText", PowersUI_GetPowerListNodeStatDescriptionHighlight(Player, GenInstanceData, "StatOnly_Name", "StatOnly_DescriptionRow",
					EntGetAttribFromPowerDefInTree(Player, "Superstats", "Innate", 0) + " " + EntGetAttribFromPowerDefInTree(Player, "Superstats_Secondary", "Innate", 0) + " " + EntGetAttribFromPowerDefInTree(Player, "Superstats_Secondary", "Innate", 1)));
 			else
 				if (GenInstanceData.IsOwned)
 					GenSetString(Gen("PowersUI_Power_Template"), "TooltipText", PowersUI_GetPowerListNodeDescription(Player, GenInstanceData, "PowersUI_Description_Owned"));
 				elif (GenInstanceData.IsAvailable)
 					GenSetString(Gen("PowersUI_Power_Template"), "TooltipText", PowersUI_GetPowerListNodeDescription(Player, GenInstanceData, "PowersUI_Description_Available"));
 				else
 					GenSetString(Gen("PowersUI_Power_Template"), "TooltipText", PowersUI_GetPowerListNodeDescription(Player, GenInstanceData, "PowersUI_Description_Unavailable"));
 				endif
 			endif

DeclareType("GenData", "PowerListNode");
GenLayoutBoxSetSelectedIndex(Self.Parent, GenInstanceNumber, False);
GenListSetSelectedRow(List, Self.Parent.Parent.State.Row);
PowersUI_SetSelectedPower(Self, GenData);
