if (GenData.Upgrade.IsRank)
	GenSetString(Self, "Power", GenGetPowerCartName(GenData));
	GenSetString(Self, "Rank", FormatInt(GenData.Upgrade.Rank + 1));
	Translate("OwnedPowers_Upgrade_Rank")
else
	GenGetPowerCartName(GenData)
endif

DeclareType("GenData", "PowerCartListNode");
if (GenData.Upgrade)
	if (GenData.Upgrade.IsRank)
		PowerNodeCanBuy(Player, GenData.PowerListNode.NodeDef, GenData.PowerListNode.GroupDef, GenData.Upgrade.Rank)
	else
		PowerNodeCanEnhance(Player, GenData.PowerListNode.TreeDef, GenData.PowerListNode.NodeDef, GenData.Upgrade.Power)
	endif
else
	GenData.PowerListNode.IsAvailable
endif

DeclareType("GenData", "PowerCartListNode");
if (GenData.Upgrade)
	if (GenData.Upgrade.IsRank)
		PowerNodeGetRank(Player, GenData.Upgrade.Node) >= GenData.Upgrade.Rank
	else
		GenGetEnhancementRank(Player, GenData.PowerListNode.NodeDef, GenData.Upgrade.Power)
	endif
else
	GenData.PowerListNode.IsOwned
endif

DeclareType("GenData", "PowerCartListNode");
if (not GenData.Upgrade)
	if (GenData.PowerListNode.IsOwned)
		GenDragStart(Self, "PowerNode", GenData.PowerListNode.NodeDef.NameFull, GenData.PowerListNode.Power.uiID, 0, "", GenData.PowerListNode.PowerIcon, MouseLeft)
	endif
endif

DeclareType("GenData", "PowerCartListNode");
GenSetString(Self, "SpentPoints", FormatInt(EnhPointsSpentOnNode(Player, PTNodeDefFromPowerCartNode(GenData))));
GenSetString(Self, "MaxPoints", FormatInt(MaxEnhPoints(PTNodeDefFromPowerCartNode(GenData))));
