if (GenData.Upgrade)
	if (GenData.Upgrade.IsRank)
		GenData.PowerListNode.Rank >= (GenData.Upgrade.Rank + 1)
	else
		GenGetEnhancementRank(Player, GenData.PowerListNode.NodeDef, GenData.Upgrade.Power)
	endif
else
	GenData.PowerListNode.IsOwned
endif

if (GenData.Upgrade)
	GenButtonClick(Gen("Advantages_PurchaseButton"));
endif

			DeclareType("GenData", "PowerCartListNode");
				GenSetState(Gen("Advantages_PurchaseButton"), User, GenData.IsEnhancement);
			if (GenData.Upgrade)
				GenSetValue(Gen("Advantages_Root"), "IsEnhancement", GenData.IsEnhancement);
				GenSetString(Gen("Advantages_Root"), "TreeName", GenData.PowerListNode.TreeDef);
				GenSetString(Gen("Advantages_Root"), "GroupName", GenData.PowerListNode.GroupDef);
				GenSetString(Gen("Advantages_Root"), "NodeName", GenData.PowerListNode.NodeDef);
				GenSetString(Gen("Advantages_Root"), "PowerName", GenData.PowerListNode.PowerDef);
				GenSetString(Gen("Advantages_Root"), "EnhancementName", GenData.Upgrade.Power);
				GenSetState(Gen("Advantages_PurchaseButton"), Disabled, not (InState(User) or InState(User1)));

				GenSetString(Gen("Advantages_Description"), "DescriptionText", GenAutoDescPowerDefByName(Player, GenData.Upgrade.Power, "AdvantageMessageKey_Name", "AttribModMessageKey"));
				if (not (InState(User) or InState(User1)))
					if (not GenData.PowerListNode.IsOwned)
						GenAppendString(Gen("Advantages_Description"), "DescriptionText", "<br><br><font style=Game_HUD_Failure>" + Translate("OwnedPowers_Upgrade_Power") + "</font>");
					elif (GenData.Upgrade.Cost > EntityGetPowerTablePoints(Player, "EnhPoints"))
						GenAppendString(Gen("Advantages_Description"), "DescriptionText", "<br><br><font style=Game_HUD_Failure>" + Translate("OwnedPowers_Upgrade_NotEnoughPoints") + "</font>");
					elif (GenData.Upgrade.Cost + EnhPointsSpentOnNode(Player, PTNodeDefFromPowerCartNode(GenData)) > MaxEnhPoints(PTNodeDefFromPowerCartNode(GenData)) )
						GenAppendString(Gen("Advantages_Description"), "DescriptionText", "<br><br><font style=Game_HUD_Failure>" + Translate("Advantages_Upgrade_TooManyEnhancements") + "</font>");
					elif (GenData.Upgrade.Cost <= EntityGetPowerTablePoints(Player, "EnhPoints"))
						if (GenData.IsRank)
							GenAppendString(Gen("Advantages_Description"), "DescriptionText", "<br><br><font style=Game_HUD_Failure>" + Translate("OwnedPowers_Upgrade_PreviousRank") + "</font>");
						else
							GenAppendString(Gen("Advantages_Description"), "DescriptionText", "<br><br><font style=Game_HUD_Failure>" + Translate("OwnedPowers_Upgrade_Requirements") + "</font>");
						endif
					endif
				endif
				GenAppendString(Gen("Advantages_Description"), "DescriptionText", "<br>" + GenAutoDescPowerDefByName(Player, GenData.Upgrade.Power, "AdvantageMessageKey_Data", "AttribModMessageKey"));

#				GenSetString(Gen("Advantages_Description"), "AdvancedDescriptionText", "");
			else
				GenSetState(Gen("Advantages_PurchaseButton"), Disabled, True);

				if (PowersUI_UseStatDescription(GenData.PowerListNode))
					GenSetString(Gen("Advantages_Description"), "DescriptionText", PowersUI_GetPowerListNodeStatDescription(Player, GenData.PowerListNode, "StatOnly_Name", "StatOnly_DescriptionRow"));
				elif (GenData.PowerListNode.IsOwned)
					GenSetString(Gen("Advantages_Description"), "DescriptionText", PowersUI_GetPowerListNodeDescription(Player, GenData.PowerListNode, "PowersUI_Description_Owned"));
				elif (GenData.PowerListNode.IsAvailable)
					GenSetString(Gen("Advantages_Description"), "DescriptionText", PowersUI_GetPowerListNodeDescription(Player, GenData.PowerListNode, "PowersUI_Description_Available"));
				else
					GenSetString(Gen("Advantages_Description"), "DescriptionText", PowersUI_GetPowerListNodeDescription(Player, GenData.PowerListNode, "PowersUI_Description_Unavailable"));
				endif

#				GenSetString(Gen("Advantages_Description"), "AdvancedDescriptionText", GenAutoDescPowerListNode(Player, GenData.PowerListNode, "PowerMessageKey", "AttribModMessageKey"));
			endif
