ExprLine.OrigStr:
if (Self.Var[PurchaseScreen].String = "OriginPoints")
	GenSendMessage(Gen("Origins_Root"), "Show");
elif (Self.Var[PurchaseScreen].String = "TreePoints")
	if(EntGetCharacterPath(Player))
		GenSendMessage(Gen("Trainer_ContentArea"), "EnterPowerSelect");
	else
		GenSendMessage(Gen("TreePowers_Root"), "Show");
	endif
elif (Self.Var[PurchaseScreen].String = "TravelPoints")
	GenSendMessage(Gen("TravelPowers_Root"), "Show");
elif (Self.Var[PurchaseScreen].String = "EnhPoints")
	GenSendMessage(Gen("Advantages_Root"), "Show");
elif (Self.Var[PurchaseScreen].String = "SuperStatPoints")
	GenSendMessage(Gen("SuperStats_Root"), "Show");
elif (Self.Var[PurchaseScreen].String = "SecondaryStatPoints")
	GenSendMessage(Gen("SecondaryStats_Root"), "Show");
elif (Self.Var[PurchaseScreen].String = "TalentPoints")
	GenSendMessage(Gen("Talents_Root"), "Show");
elif (Self.Var[PurchaseScreen].String = "SpecializationPoints")
	GenSendMessage(Gen("SpecTree_Window_Root"), "Show");
endif
