	GenSetValue(Self, "DurationMinutes", 0);
	guildevent_SetDuration((3600 * Self.Var[DurationHours].int) + (60 * Self.Var[DurationMinutes].int));
	GenSendMessage(Self, "GetMonthYear"); 
else
