ExprLine.OrigStr:
DeclareType("RowData", "ClientPlayerStruct");
if (RowData.friend = 1 and RowData.Guildmate =0)
	GenSetString(Self, "Text", "<font style=Social_Player_Name_Normal>" + RowData.Name + "</font>" + "<font style=Social_Player_Handle_Friend>" + RowData.Handle + "</font>");
elif (RowData.friend = 0 and RowData.Guildmate =1)
	GenSetString(Self, "Text", "<font style=Social_Player_Name_In_Same_SuperGroup>" + RowData.Name + "</font>" + "<font style=Social_Player_Handle_Normal>" + RowData.Handle + "</font>");
elif (RowData.friend = 1 and RowData.Guildmate =1)
	GenSetString(Self, "Text", "<font style=Social_Player_Name_In_Same_SuperGroup>" + RowData.Name + "</font>" + "<font style=Social_Player_Handle_friend>" + RowData.Handle + "</font>");
elif (RowData.ignored = 1)
	GenSetString(Self, "Text", "<font style=Social_Player_Name_Normal>" + RowData.Name + "</font>" + "<font style=Social_Player_Handle_Ignored>" + RowData.Handle + "</font>");
else
	GenSetString(Self, "Text", "<font style=Social_Player_Name_Normal>" + RowData.Name + "</font>" + "<font style=Social_Player_Handle_Normal>" + RowData.Handle + "</font>");
endif
