				if (InState(User) and EntityGetPowerTablePointsSpent(GenGetFakePlayer(), "Specializationpoints") < LayoutBox.Parent.Var[UnlockPoints].Int + 10)
					if (GenInstanceData.Rank = 0)
						Translate("SpecTree_Window_SpendPoints")
