						SpecTree_Tooltip(Player, GenInstanceData, "SpecTreePowerCurrentRankMessageKey", "SpecTreePowerNextRankMessageKey", "SpecTreeAttribModMessageKey");
					endif
				endif
			else
				""
			endif

